CREATE OR REPLACE PROCEDURE intpd_processar_fila_agrup (ie_evento_p text) AS $body$
DECLARE


nr_sequencia_w	intpd_fila_transmissao.nr_sequencia%type;

c01 CURSOR FOR
SELECT	distinct
	nr_seq_agrupador,
	ie_operacao,
	nr_seq_evento_sistema
from	intpd_fila_transmissao
where	ie_status = 'A'
and	ie_evento = ie_evento_p;

c01_w	c01%rowtype;


BEGIN

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	min(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	intpd_fila_transmissao
	where	ie_status 		= 'A'
	and	ie_evento 		= ie_evento_p
	and	nr_seq_agrupador	= c01_w.nr_seq_agrupador
	and	ie_operacao		= c01_w.ie_operacao
	and	nr_seq_evento_sistema	= c01_w.nr_seq_evento_sistema;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

		update	intpd_fila_transmissao
		set	ie_status 		= 'P',
			nr_seq_dependencia	 = NULL
		where	nr_sequencia		= nr_sequencia_w;

		update	intpd_fila_transmissao
		set	nr_seq_dependencia 	= nr_sequencia_w
		where	ie_status 		= 'A'
		and	ie_evento 		= ie_evento_p
		and	nr_seq_agrupador	= c01_w.nr_seq_agrupador
		and	ie_operacao		= c01_w.ie_operacao
		and	nr_seq_evento_sistema	= c01_w.nr_seq_evento_sistema
		and	nr_sequencia		<> nr_sequencia_w;

	end if;

end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
