ds_erro_w			varchar(4000);
ie_erro_w				varchar(1) := 'N';
ie_exception_w			varchar(1) := 'N';
i				integer;
qt_grupo_proc_w			bigint;
grupo_proc_w			grupo_proc%rowtype;

c01 CURSOR FOR
SELECT	*
from	xmltable('/STRUCTURE/PROCEDURE_GROUP' passing xml_p columns
	ie_action				varchar(40)		path		'IE_ACTION',
	cd_especialidade			bigint		path		'CD_SPECIALTY',
	cd_grupo_proc			bigint		path		'CD_PROCEDURE_GROUP',
	ds_grupo_proc			varchar(240)		path		'DS_GROUP',
	ds_observacao			varchar(2000)		path		'DS_OBSERVATION',
	cd_sistema_ant			varchar(80)		path		'NR_EXTERNAL_DOCUMENT',
	dt_atualizacao			varchar(14)		path		'DT_UPDATE',
	ie_situacao			varchar(1)		path		'IE_STATUS',
	nm_usuario			varchar(15)		path		'NM_USER');

c01_w	c01%rowtype;


BEGIN

/*Atualiza o status da fila para Em processamento*/

update	intpd_fila_transmissao
set	ie_status = 'R'
where	nr_sequencia = nr_sequencia_p;

commit;

begin

select	coalesce(b.ie_conversao,'I'),
	nr_seq_sistema,
	nr_seq_projeto_xml,
	nr_seq_regra_conv
into STRICT	ie_conversao_w,
	nr_seq_sistema_w,
	nr_seq_projeto_xml_w,
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_sequencia_p;

ie_sistema_externo_w			:=	nr_seq_sistema_w;
reg_integracao_w.nr_seq_fila_transmissao	:=	nr_sequencia_p;
reg_integracao_w.ie_envio_recebe		:=	'R';
reg_integracao_w.ie_sistema_externo		:=	ie_sistema_externo_w;
reg_integracao_w.ie_conversao		:=	ie_conversao_w;
reg_integracao_w.nr_seq_projeto_xml		:=	nr_seq_projeto_xml_w;
reg_integracao_w.nr_seq_regra_conversao	:=	nr_seq_regra_w;
reg_integracao_w.intpd_log_receb.delete;
reg_integracao_w.qt_reg_log			:=	0;

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	begin
	ie_erro_w				:= 	'N';
	reg_integracao_w.nm_tabela		:=	'GRUPO_PROC';
	reg_integracao_w.nm_elemento	:=	'PROCEDURE_GROUP';
	reg_integracao_w.nr_seq_visao	:=	27759; /*HTML5 - Swing - CorAte_F7 - Procedimentos*/
	if (coalesce(upper(c01_w.ie_action),'INSERT') <> 'DELETE') then /*INSERT ou UPDATE*/
		if (coalesce(upper(c01_w.ie_action),'INSERT') = 'UPDATE') then

			select 	count(*)
			into STRICT	qt_grupo_proc_w
			from 	grupo_proc
			where 	cd_grupo_proc = c01_w.cd_grupo_proc;

			if (qt_grupo_proc_w = 0) then
				/*Código do grupo procedimento inválido ou inexistente no Tasy para alteração ou exclusão do registro.*/

				intpd_gravar_log_recebimento(nr_sequencia_p,wheb_mensagem_pck.get_texto(1021360),'INTPDTASY','0004');
				ie_erro_w := 'S';
			end if;
		end if;

		if (ie_erro_w = 'N') then

			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'CD_ESPECIALIDADE', c01_w.cd_especialidade, 'S', grupo_proc_w.cd_especialidade) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; grupo_proc_w.cd_especialidade := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'DS_GRUPO_PROC', c01_w.ds_grupo_proc, 'N', grupo_proc_w.ds_grupo_proc) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; grupo_proc_w.ds_grupo_proc := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'DS_OBSERVACAO', c01_w.ds_observacao, 'N', grupo_proc_w.ds_observacao) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; grupo_proc_w.ds_observacao := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'CD_SISTEMA_ANT', c01_w.cd_sistema_ant, 'N', grupo_proc_w.cd_sistema_ant) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; grupo_proc_w.cd_sistema_ant := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'DT_ATUALIZACAO', c01_w.dt_atualizacao, 'N', grupo_proc_w.dt_atualizacao) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; grupo_proc_w.dt_atualizacao := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'IE_SITUACAO', c01_w.ie_situacao, 'N', grupo_proc_w.ie_situacao) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; grupo_proc_w.ie_situacao := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'NM_USUARIO', c01_w.nm_usuario, 'N', grupo_proc_w.nm_usuario) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; grupo_proc_w.nm_usuario := _ora2pg_r.ds_valor_retorno_p;

			if (reg_integracao_w.qt_reg_log = 0) then

				select	max(ie_origem_proced)
				into STRICT	grupo_proc_w.ie_origem_proced
				from 	especialidade_proc
				where	cd_especialidade = grupo_proc_w.cd_especialidade;

				select	max(cd_grupo_proc)
				into STRICT	grupo_proc_w.cd_grupo_proc
				from	grupo_proc
				where 	cd_grupo_proc = c01_w.cd_grupo_proc;

				if (coalesce(grupo_proc_w.cd_grupo_proc,0) > 0) then

					grupo_proc_w.cd_original := grupo_proc_w.cd_grupo_proc;

					update	grupo_proc
					set	row = grupo_proc_w
					where	cd_grupo_proc = grupo_proc_w.cd_grupo_proc;
				else
					select 	max(cd_grupo_proc) + 1
					into STRICT	grupo_proc_w.cd_grupo_proc
					from 	grupo_proc;

					grupo_proc_w.cd_original := grupo_proc_w.cd_grupo_proc;

					insert into grupo_proc values (grupo_proc_w.*);

					if (nr_seq_regra_w > 0) then
						CALL gerar_conv_meio_externo(null, 'GRUPO_PROC', 'CD_GRUPO_PROC', grupo_proc_w.cd_grupo_proc, c01_w.cd_sistema_ant, null, nr_seq_regra_w, 'A', grupo_proc_w.nm_usuario);
					end if;
				end if;
			end if;
		end if;

	elsif (coalesce(upper(c01_w.ie_action),'INSERT') = 'DELETE') then /*DELETE*/
		select 	count(*)
		into STRICT	qt_grupo_proc_w
		from 	grupo_proc
		where 	cd_grupo_proc = c01_w.cd_grupo_proc;

		if (qt_grupo_proc_w > 0) then
			delete	FROM grupo_proc
			where	cd_grupo_proc = c01_w.cd_grupo_proc;
		else
			/*Código do grupo procedimento inválido ou inexistente no Tasy para alteração ou exclusão do registro.*/

			intpd_gravar_log_recebimento(nr_sequencia_p,wheb_mensagem_pck.get_texto(1021360),'INTPDTASY','0004');
			ie_erro_w := 'S';
		end if;

	end if;
	end;
end loop;
close c01;

exception
when others then
	begin
	ds_erro_w := substr(dbms_utility.format_error_backtrace || chr(13) || chr(10) || sqlerrm,1,4000);
	rollback;

	update	intpd_fila_transmissao
	set	ie_status 	 	= 'E',
		cd_default_message 	= CASE WHEN ds_erro_w = NULL THEN null  ELSE '0005' END ,
		ds_log 		= ds_erro_w,
		nr_doc_externo	= c01_w.cd_sistema_ant
	where	nr_sequencia 	= nr_sequencia_p;

	ie_exception_w := 'S';

	end;
end;

if (ie_exception_w = 'N') then

	if	((reg_integracao_w.qt_reg_log > 0) or (coalesce(ie_erro_w,'N') = 'S')) then
		begin
		rollback;

		update	intpd_fila_transmissao
		set	ie_status		= 'E',
			cd_default_message 	= CASE WHEN ds_erro_w = NULL THEN null  ELSE '0005' END ,
			ds_log 		= ds_erro_w,
			nr_doc_externo	= c01_w.cd_sistema_ant
		where	nr_sequencia 	= nr_sequencia_p;

		for i in 0..reg_integracao_w.qt_reg_log-1 loop
			intpd_gravar_log_recebimento(nr_sequencia_p,reg_integracao_w.intpd_log_receb[i].ds_log,'INTPDTASY',reg_integracao_w.intpd_log_receb[i].cd_default_message);
		end loop;
		end;
	else
		update	intpd_fila_transmissao
		set	ie_status		= 'S',
			cd_default_message 	= '0000',
			nr_seq_documento	= coalesce(grupo_proc_w.cd_grupo_proc, c01_w.cd_grupo_proc),
			nr_doc_externo	= c01_w.cd_sistema_ant
		where	nr_sequencia	= nr_sequencia_p;
	end if;

end if;

commit;

end;
$body$
