CREATE OR REPLACE PROCEDURE intpd_receber_subgrupo_mat ( nr_sequencia_p bigint, xml_p xml) AS $body$
DECLARE



_ora2pg_r RECORD;
ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_projeto_xml_w		intpd_eventos_sistema.nr_seq_projeto_xml%type;
nr_seq_sistema_w		intpd_eventos_sistema.nr_seq_sistema%type;
ie_sistema_externo_w		varchar(15);
reg_integracao_w		gerar_int_padrao.reg_integracao_conv;
nr_seq_regra_w			conversao_meio_externo.nr_seq_regra%type;
ds_erro_w			varchar(4000);
ie_erro_w 			varchar(1) := 'N';
i				integer;
subgrupo_material_w		subgrupo_material%rowtype;
qt_subgrupo_material_w			bigint;

c01 CURSOR FOR
SELECT	*
from	xmltable('/STRUCTURE/MATERIAL_SUBGROUP' passing xml_p columns
	ie_action							varchar(40)	path		'IE_ACTION',
	cd_accounting_account	varchar(20)	path		'CD_ACCOUNTING_ACCOUNT',
	cd_material_group			smallint			path		'CD_MATERIAL_GROUP',
	cd_material_subgroup	smallint			path		'CD_MATERIAL_SUBGROUP',
	ds_subgroup						varchar(255)	path		'DS_SUBGROUP',
	nr_external_document	varchar(80)	path		'NR_EXTERNAL_DOCUMENT',
	dt_update							varchar(14)	path		'DT_UPDATE',
	ie_situation					varchar(1)		path		'IE_SITUATION',
	nm_user								varchar(15)	path		'NM_USER');

c01_w	c01%rowtype;


BEGIN

update	intpd_fila_transmissao
set	ie_status = 'R'
where	nr_sequencia = nr_sequencia_p;

commit;

begin
select	coalesce(b.ie_conversao,'I'),
	nr_seq_sistema,
	nr_seq_projeto_xml,
	nr_seq_regra_conv
into STRICT	ie_conversao_w,
	nr_seq_sistema_w,
	nr_seq_projeto_xml_w,
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_sequencia_p;

ie_sistema_externo_w	:=	nr_seq_sistema_w;

reg_integracao_w.nr_seq_fila_transmissao	:=	nr_sequencia_p;
reg_integracao_w.ie_envio_recebe		:=	'R';
reg_integracao_w.ie_sistema_externo		:=	ie_sistema_externo_w;
reg_integracao_w.ie_conversao			:=	ie_conversao_w;
reg_integracao_w.nr_seq_projeto_xml		:=	nr_seq_projeto_xml_w;
reg_integracao_w.nr_seq_regra_conversao		:=	nr_seq_regra_w;
reg_integracao_w.intpd_log_receb.delete;
reg_integracao_w.qt_reg_log			:=	0;

open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	begin
	ie_erro_w				:= 	'N';
	reg_integracao_w.nm_tabela		:=	'SUBGRUPO_MATERIAL';
	reg_integracao_w.nm_elemento		:=	'MATERIAL_SUBGROUP';

	c01_w.ie_action := coalesce(upper(c01_w.ie_action),'INSERT');

	if (c01_w.ie_action = 'DELETE') then
		select count(*)
		into STRICT qt_subgrupo_material_w
		from subgrupo_material
		where cd_grupo_material = c01_w.cd_material_group
		and cd_subgrupo_material = c01_w.cd_material_subgroup;

		if (qt_subgrupo_material_w = 0) then
			intpd_gravar_log_recebimento(nr_sequencia_p,wheb_mensagem_pck.get_texto(1012755),'INTPDTASY','0004');
			ie_erro_w := 'S';
		else
			begin
			delete
			from subgrupo_material
			where cd_grupo_material = c01_w.cd_material_group
			and cd_subgrupo_material = c01_w.cd_material_subgroup;

			exception
				when others then
					rollback;

					update subgrupo_material
					set ie_situacao = 'I'
					where cd_grupo_material = c01_w.cd_material_group
					and cd_subgrupo_material = c01_w.cd_material_subgroup;
			end;
			subgrupo_material_w.cd_grupo_material := null;
			subgrupo_material_w.cd_subgrupo_material := null;

		end if;
	else -- INSERT or UPDATE
		if (c01_w.ie_action = 'UPDATE') then
			select 	count(*)
			into STRICT	qt_subgrupo_material_w
			from 	subgrupo_material
			where cd_grupo_material = c01_w.cd_material_group
			and cd_subgrupo_material = c01_w.cd_material_subgroup;

			if (qt_subgrupo_material_w = 0) then
				intpd_gravar_log_recebimento(nr_sequencia_p,wheb_mensagem_pck.get_texto(1012755),'INTPDTASY','0004');
				ie_erro_w := 'S';
			end if;
		end if;

		if (ie_erro_w = 'N') then
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'CD_CONTA_CONTABIL', c01_w.cd_accounting_account, 'N', subgrupo_material_w.cd_conta_contabil) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.cd_conta_contabil := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'CD_GRUPO_MATERIAL', c01_w.cd_material_group, 'S', subgrupo_material_w.cd_grupo_material) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.cd_grupo_material := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'CD_SUBGRUPO_MATERIAL', c01_w.cd_material_subgroup, 'N', subgrupo_material_w.cd_subgrupo_material) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.cd_subgrupo_material := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'DS_SUBGRUPO_MATERIAL', c01_w.ds_subgroup, 'N', subgrupo_material_w.ds_subgrupo_material) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.ds_subgrupo_material := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'CD_SISTEMA_ANT', c01_w.nr_external_document, 'N', subgrupo_material_w.cd_sistema_ant) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.cd_sistema_ant := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'DT_ATUALIZACAO', c01_w.dt_update, 'N', subgrupo_material_w.dt_atualizacao) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.dt_atualizacao := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'IE_SITUACAO', c01_w.ie_situation, 'N', subgrupo_material_w.ie_situacao) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.ie_situacao := _ora2pg_r.ds_valor_retorno_p;
			SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'NM_USUARIO', c01_w.nm_user, 'N', subgrupo_material_w.nm_usuario) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; subgrupo_material_w.nm_usuario := _ora2pg_r.ds_valor_retorno_p;

			if (reg_integracao_w.qt_reg_log = 0) then

				select	max(cd_subgrupo_material)
				into STRICT	qt_subgrupo_material_w
				from	subgrupo_material
				where cd_grupo_material = c01_w.cd_material_group
				and cd_subgrupo_material = c01_w.cd_material_subgroup;

				if (coalesce(qt_subgrupo_material_w,0) > 0) then

					update	subgrupo_material
					set	row = subgrupo_material_w
					where	cd_grupo_material = subgrupo_material_w.cd_grupo_material
					and cd_subgrupo_material = subgrupo_material_w.cd_subgrupo_material;

				else

					insert into subgrupo_material values (subgrupo_material_w.*);

					/*'Registra a conversão que poderá ser utilizada em outros pontos de integração e para somente atualizar (update) num recebimento futuro do mesmo registro.'*/

					if (nr_seq_regra_w > 0) then
						CALL gerar_conv_meio_externo(null, 'SUBGRUPO_MATERIAL', 'CD_SUBGRUPO_MATERIAL', subgrupo_material_w.cd_grupo_material, c01_w.nr_external_document, null, nr_seq_regra_w, 'A', subgrupo_material_w.nm_usuario);
					end if;
				end if;
			end if;
		end if;
	end if;
	end;
end loop;
close c01;

exception
when others then
	begin
	ds_erro_w	:=	substr(dbms_utility.format_error_backtrace || chr(13) || chr(10) || sqlerrm,1,4000);
	rollback;

	update	intpd_fila_transmissao
	set	ie_status = 'E',
		cd_default_message 	= CASE WHEN ds_erro_w = NULL THEN null  ELSE '0005' END ,
		ds_log = ds_erro_w,
		nr_doc_externo = c01_w.nr_external_document
	where	nr_sequencia = nr_sequencia_p;
	end;
end;

if	((reg_integracao_w.qt_reg_log > 0) or (ie_erro_w = 'S')) then
	begin
	rollback;

	update	intpd_fila_transmissao
	set	ie_status = 'E',
		cd_default_message 	= CASE WHEN ds_erro_w = NULL THEN null  ELSE '0005' END ,
		ds_log = ds_erro_w,
		nr_doc_externo = c01_w.nr_external_document
	where	nr_sequencia = nr_sequencia_p;

	for i in 0..reg_integracao_w.qt_reg_log-1 loop
		intpd_gravar_log_recebimento(nr_sequencia_p,reg_integracao_w.intpd_log_receb[i].ds_log,'INTPDTASY',reg_integracao_w.intpd_log_receb[i].cd_default_message);
	end loop;
	end;
else
	update	intpd_fila_transmissao
	set	ie_status = 'S',
		cd_default_message = '0000',
		nr_seq_documento = coalesce(subgrupo_material_w.cd_subgrupo_material,c01_w.cd_material_subgroup),
		nr_doc_externo 	 = c01_w.nr_external_document
	where	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
