		SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'NR_SEQ_TRANS_FIN', c02_w.nr_seq_trans_fin, 'S', titulo_pagar_classif_w.nr_seq_trans_fin ) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; titulo_pagar_classif_w.nr_seq_trans_fin  := _ora2pg_r.ds_valor_retorno_p;
		SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'VL_ACRESCIMO', c02_w.vl_acrescimo, 'N', titulo_pagar_classif_w.vl_acrescimo ) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; titulo_pagar_classif_w.vl_acrescimo  := _ora2pg_r.ds_valor_retorno_p;
		SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'VL_DESCONTO', c02_w.vl_desconto, 'N', titulo_pagar_classif_w.vl_desconto ) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; titulo_pagar_classif_w.vl_desconto  := _ora2pg_r.ds_valor_retorno_p;	
		SELECT * FROM intpd_processar_atributo(reg_integracao_w, 'VL_TITULO', c02_w.vl_titulo, 'N', titulo_pagar_classif_w.vl_titulo ) INTO STRICT _ora2pg_r;
 reg_integracao_w := _ora2pg_r.reg_integracao_p; titulo_pagar_classif_w.vl_titulo  := _ora2pg_r.ds_valor_retorno_p;
		
		if (reg_integracao_w.qt_reg_log = 0) then
			begin
			
			select	coalesce(max(a.nr_sequencia),0) + 1
			into STRICT	titulo_pagar_classif_w.nr_sequencia
			from	titulo_pagar_classif a
			where	a.nr_titulo = titulo_pagar_w.nr_titulo;
			
			titulo_pagar_classif_w.nr_titulo := titulo_pagar_w.nr_titulo;
			
			insert into titulo_pagar_classif values (titulo_pagar_classif_w.*);
			
			end;	
		end if;	
		
		end;
	end loop;
	close C02;
	
	end;
end loop;
close C01;
exception
when others then
	begin
	/*'Em caso de qualquer falha o sistema captura a mensagem de erro, efetua o rollback, atualiza o status para Erro e registra a falha ocorrida'*/

	ds_erro_w	:=	substr(sqlerrm,1,4000);
	rollback;
	update	intpd_fila_transmissao
	set		ie_status 		= 'E',
			ds_log 			= ds_erro_w
	where	nr_sequencia 	= nr_sequencia_p;
	end;
end;

if (reg_integracao_w.qt_reg_log > 0) then
	begin
	/*'Em caso de qualquer consistencia o sistema efetua rollback, atualiza o status para Erro e registra todos os logs de consistecia'*/

	rollback;
	
	update	intpd_fila_transmissao
	set		ie_status 		= 'E',
			ds_log 			= ds_erro_w
	where	nr_sequencia 	= nr_sequencia_p;
	
	for i in 0..reg_integracao_w.qt_reg_log-1 loop
		intpd_gravar_log_recebimento(nr_sequencia_p,reg_integracao_w.intpd_log_receb[i].ds_log,'INTPDTASY');
	end loop;
	end;
else
	update	intpd_fila_transmissao
	set	ie_status 	 = 'S',
		cd_default_message = '0000',		
		nr_seq_documento = titulo_pagar_w.nr_titulo
	where	nr_sequencia	 = nr_sequencia_p;
end if;

commit;

end;
$body$
