CREATE OR REPLACE PROCEDURE intpd_reg_integracao_inicio ( nr_seq_fila_p bigint, ie_envio_recebe_p text, reg_integracao_p INOUT gerar_int_padrao.reg_integracao_conv) AS $body$
DECLARE


ie_conversao_w			intpd_eventos_sistema.ie_conversao%type;
nr_seq_sistema_w			intpd_eventos_sistema.nr_seq_sistema%type;
nr_seq_projeto_xml_w		intpd_eventos_sistema.nr_seq_projeto_xml%type;
nr_seq_regra_conv_w		intpd_eventos_sistema.nr_seq_regra_conv%type;
cd_estab_documento_w		intpd_fila_transmissao.cd_estab_documento%type;



BEGIN
CALL intpd_inicializacao(nr_seq_fila_p);
begin
select	coalesce(b.ie_conversao,'I'),
	b.nr_seq_sistema,
	b.nr_seq_projeto_xml,
	b.nr_seq_regra_conv,
	coalesce(a.cd_estab_documento, obter_estabelecimento_ativo)
into STRICT	ie_conversao_w,
	nr_seq_sistema_w,
	nr_seq_projeto_xml_w,
	nr_seq_regra_conv_w,
	cd_estab_documento_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia = nr_seq_fila_p;
exception
when others then
	nr_seq_sistema_w	:=	null;
end;

reg_integracao_p.intpd_log_receb.delete;
reg_integracao_p.qt_reg_log			:=	0;
reg_integracao_p.nr_seq_fila_transmissao	:=	nr_seq_fila_p;
reg_integracao_p.ie_envio_recebe		:=	ie_envio_recebe_p;
reg_integracao_p.ie_sistema_externo		:=	nr_seq_sistema_w;
reg_integracao_p.ie_conversao		:=	ie_conversao_w;
reg_integracao_p.nr_seq_projeto_xml		:=	nr_seq_projeto_xml_w;
reg_integracao_p.nr_seq_regra_conversao	:=	nr_seq_regra_conv_w;
reg_integracao_p.ie_marca_verif		:= 	coalesce(reg_integracao_p.ie_marca_verif, 'X');

if (substr(obter_se_integracao_ish(nr_seq_projeto_xml_w),1,1) = 'S') then
	begin
	reg_integracao_p.ie_obrigatoriedade_tabela	:=	'S';
	reg_integracao_p.ie_regra_valor_default		:= 	'S';
	
	/*During the receiving processes it is done on the ish_converter_response object*/

	if (ie_envio_recebe_p = 'E') then
		CALL ish_param_pck.set_cd_estab_ant(obter_estabelecimento_ativo);
		CALL wheb_usuario_pck.set_cd_estabelecimento(cd_estab_documento_w);
	end if;
	end;
else	
	begin
	reg_integracao_p.ie_obrigatoriedade_tabela	:= 	coalesce(reg_integracao_p.ie_obrigatoriedade_tabela, 'N');
	reg_integracao_p.ie_regra_valor_default		:= 	coalesce(reg_integracao_p.ie_regra_valor_default, 'N');
	end;
end if;

EXECUTE 'alter session set nls_date_format = ''yyyy/mm/dd hh24:mi:ss''';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
