		where	nr_sequencia	= nr_sequencia_p;
		/*and 	((cd_estab_documento	= cd_estab_documento_w) or (cd_estab_documento_w = 'X'));*/

end if;

if ('S' = coalesce(ie_commit_p, 'S')) then
	commit;
end if;

end;
