CREATE OR REPLACE PROCEDURE intpd_update_async_evt_status ( nr_seq_fila_p text, ds_status_p text) AS $body$
DECLARE

ie_evento_w varchar(15);

/* 161 */

nr_seq_material_w	material_atend_paciente.nr_sequencia%type;
nr_seq_propaci_w	procedimento_paciente.nr_sequencia%type;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
nr_seq_item_documento_w	intpd_fila_transmissao.nr_seq_item_documento%type;

BEGIN

select  ie_evento,
	nr_seq_documento,
	nr_seq_item_documento
into STRICT    ie_evento_w,
	nr_seq_documento_w,
	nr_seq_item_documento_w
from    intpd_fila_transmissao
where   nr_sequencia = nr_seq_fila_p;

if (ie_evento_w = '161') then -- Send Care Expenses
	begin

	if (nr_seq_item_documento_w = 1) then -- material
		select	nr_sequencia,
			nr_atendimento
		into STRICT	nr_seq_material_w,
			nr_atendimento_w
		from	material_atend_paciente
		where	nr_sequencia = nr_seq_documento_w;
	elsif (nr_seq_item_documento_w = 2) then -- procedimento
		select 	nr_sequencia,
			nr_atendimento
		into STRICT   	nr_seq_propaci_w,
			nr_atendimento_w
		from   	procedimento_paciente
		where  	nr_sequencia = nr_seq_documento_w;
	elsif (nr_seq_item_documento_w = 3) then -- devolução de material
		select	a.nr_sequencia,
			a.nr_atendimento
		into STRICT	nr_seq_material_w,
			nr_atendimento_w
		from	material_atend_paciente a,
			intpd_devol_mat_proc b
		where	a.nr_sequencia = b.nr_seq_matpaci
		and	b.nr_sequencia = nr_seq_documento_w;
	elsif (nr_seq_item_documento_w = 4) then -- devolução de procedimento
		select 	a.nr_sequencia,
			a.nr_atendimento
		into STRICT   	nr_seq_propaci_w,
			nr_atendimento_w
		from   	procedimento_paciente a,
			intpd_devol_mat_proc b
		where  	a.nr_sequencia = b.nr_seq_propaci
		and	b.nr_sequencia = nr_seq_documento_w;
	end if;

	insert into status_integr_conta_pac(
		nr_sequencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_atendimento,
		nr_seq_matpaci,
		nr_seq_propaci,
		ie_status,
		nr_seq_fila_transm)
	values(	nextval('status_integr_conta_pac_seq'),
		clock_timestamp(),
		clock_timestamp(),
		'WEBSERVICE',
		'WEBSERVICE',
		nr_atendimento_w,
		nr_seq_material_w,
		nr_seq_propaci_w,
		(case when upper(ds_status_p) = 'TRUE' then 'S' else 'E' end),
		nr_seq_fila_p);
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
