CREATE OR REPLACE PROCEDURE int_atualizar_obs_agenda ( nr_sequencia_p bigint, ds_observacao_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
BEGIN

begin
if (coalesce(nr_sequencia_p,0) > 0) then
	update	agenda_consulta
	set		ds_observacao	=	substr(ds_observacao_p,1,2000),
			dt_atualizacao	=	clock_timestamp(),
			nm_usuario		=	nm_usuario_p
	where	nr_sequencia	=	nr_sequencia_p;
	commit;
end if;

exception
when others then
	ds_erro_p	:= substr(SQLERRM,1,255);
end;

end;
