CREATE OR REPLACE PROCEDURE inutilizar_coleta_hemot ( nr_seq_coleta_p bigint, nr_seq_tipo_p bigint, qt_procedimento_p bigint, nr_seq_exame_p bigint, cd_setor_atendimento_p bigint, nr_atendimento_p bigint, ie_tipo_baixa_p text, ie_valor_informado_p text, cd_medico_p text, nr_seq_atepacu_p bigint, dt_procedimento_p timestamp, cd_convenio_p bigint, cd_categoria_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_motivo_inut_p text default null) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_proc_interno_w	bigint;


BEGIN

select	cd_procedimento,
	ie_origem_proced,
	nr_seq_proc_interno
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w
from	san_tipo_doacao
where	nr_sequencia	= nr_seq_tipo_p;

if (coalesce(nr_seq_atepacu_p,0) = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(303204);
end if;

nr_sequencia_w := inserir_procedimento_paciente(
	cd_procedimento_w, qt_procedimento_p, nr_seq_exame_p, nr_seq_proc_interno_w, ie_origem_proced_w, cd_setor_atendimento_p, nr_atendimento_p, cd_estabelecimento_p, nm_usuario_p, ie_tipo_baixa_p, ie_valor_informado_p, cd_medico_p, nr_seq_atepacu_p, dt_procedimento_p, cd_convenio_p, cd_categoria_p, nr_sequencia_w);

update	san_doacao
set	dt_inutilizacao	= clock_timestamp(),
	nm_usuario_inut      = nm_usuario_p
where	nr_sequencia	= nr_seq_coleta_p;

if (ds_motivo_inut_p IS NOT NULL AND ds_motivo_inut_p::text <> '') then
	update	san_doacao
	set 	ds_motivo_inutil = SUBSTR(ds_motivo_inut_p,1,255)
	where 	nr_sequencia 	= nr_seq_coleta_p;
end if;

commit;

end;
$body$
