CREATE OR REPLACE PROCEDURE inutilizar_hemocomp_producao ( nr_seq_producao_p bigint, nr_seq_inutilizacao_p bigint, nm_pasta_p text, nm_usuario_p text) AS $body$
DECLARE

nm_pasta_w	varchar(3);


BEGIN

/*
(_TS)	- DELPHI
(WJP) 	 JAVA
*/
if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
	
	if (upper(nm_pasta_p) = 'EXAMES_TS' or upper(nm_pasta_p) = 'EXAMESWJP') then					nm_pasta_w := 'E';
	elsif (upper(nm_pasta_p) = 'PRODUCAO_ITENS_EMPRESTADOS_TS' or upper(nm_pasta_p) = 'PRODUCAOITENSEMPRESTADOSWJP') then	nm_pasta_w := 'IE';
	elsif (upper(nm_pasta_p) = 'PRODUCAO_TS' or upper(nm_pasta_p) = 'PRODUCAOWJP') then					nm_pasta_w := 'P';	
	elsif (upper(nm_pasta_p) = 'ESTOQUE_TS' or upper(nm_pasta_p) = 'ESTOQUEWJP') then					nm_pasta_w := 'Q';
	elsif (upper(nm_pasta_p) = 'RESERVA_TS' or upper(nm_pasta_p) = 'RESERVAWJP') then					nm_pasta_w := 'R';
	elsif (upper(nm_pasta_p) = 'EMPREST_SAIDA_TS' or upper(nm_pasta_p) = 'EMPRESTSAIDAWJP') then				nm_pasta_w := 'S';
	elsif (upper(nm_pasta_p) = 'TRANSFUSAO_TS' or upper(nm_pasta_p) = 'TRANSFUSAOWJP') then				nm_pasta_w := 'T';
	elsif (upper(nm_pasta_p) = 'AGRUPAMENTO_TS' or upper(nm_pasta_p) = 'AGRUPAMENTOWJP') then				nm_pasta_w := 'A';
	elsif (upper(nm_pasta_p) = 'CONSULTAWJP') then									nm_pasta_w := 'C';
	end if;

	update 	san_producao
	set	nr_seq_inutil 		= nr_seq_inutilizacao_p,
		ie_local_inutilizacao	= nm_pasta_w,
		dt_inutilizacao		= clock_timestamp(),
		nm_usuario_inut		= nm_usuario_p
	where	nr_sequencia		= nr_seq_producao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
