SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inutilizar_senhas_fila (nm_usuario_p text, cd_estabelecimento_p bigint, hr_inutilizacao_senha_p bigint default null) AS $body$
DECLARE


hr_atual_w			varchar(20);
hr_inutilizacao_w		varchar(20);
nr_sequencia_w			bigint;
nr_seq_senha_w			bigint;	
ie_considera_fim_atend_w	varchar(1);

c01 CURSOR FOR
	SELECT	to_char(HR_INUTILIZACAO_SENHA,'hh24:mi'),
		to_char(clock_timestamp(),'hh24:mi'),
		nr_sequencia
	from	FILA_ESPERA_SENHA
	where	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(ie_situacao,'A') = 'A';	


c02 CURSOR FOR
	SELECT  a.nr_sequencia
	FROM    paciente_senha_fila a,          
		fila_espera_senha b    
	WHERE   a.nr_seq_fila_senha = b.nr_sequencia   
	AND     coalesce(a.NR_SEQ_FILA_SENHA, a.NR_SEQ_FILA_SENHA_origem) = nr_sequencia_w   
	and	a.cd_estabelecimento = cd_estabelecimento_p
	AND     (((ie_considera_fim_atend_w = 'N') and (coalesce(a.dt_vinculacao_senha::text, '') = ''))
	Or (coalesce(a.dt_inicio_atendimento::text, '') = ''))
	AND     coalesce(a.dt_inutilizacao::text, '') = ''  
	AND     coalesce(a.dt_utilizacao::text, '') = ''  
	and	obter_se_existe_triagem_senha(a.nr_sequencia) = 'N'
	AND     ((coalesce(a.qt_chamadas,0) <= 0) OR (0 = 0) OR (coalesce(dt_inutilizacao::text, '') = ''))
	AND (coalesce(hr_inutilizacao_senha_p::text, '') = '' or dt_geracao_senha < (clock_timestamp() - hr_inutilizacao_senha_p/24));

BEGIN

ie_considera_fim_atend_w := Obter_Param_Usuario(10021, 126, Obter_perfil_Ativo, nm_usuario_p, cd_estabelecimento_p, ie_considera_fim_atend_w);

open C01;
loop
fetch C01 into	
	hr_inutilizacao_w,
	hr_atual_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (hr_inutilizacao_w =  hr_atual_w) then
	
		begin
		
		open C02;
		loop
		fetch C02 into	
			nr_seq_senha_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			
			update	paciente_senha_fila
			set 	dt_inutilizacao 	= clock_timestamp(),
				nm_usuario_inutilizacao = wheb_usuario_pck.get_nm_usuario,
				ds_maquina_inutilizacao = wheb_usuario_pck.get_nm_maquina
			where	nr_sequencia = nr_seq_senha_w;
			
			end;
		end loop;	
		
		close C02;
		end;
	end if;
	
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE inutilizar_senhas_fila (nm_usuario_p text, cd_estabelecimento_p bigint, hr_inutilizacao_senha_p bigint default null) FROM PUBLIC;

