CREATE OR REPLACE PROCEDURE inutilizar_senha_whyline ( nr_subscription_id_p paciente_senha_fila.nr_sequencia%type, nm_usuario_p paciente_senha_fila.nm_usuario_inutilizacao%type) AS $body$
DECLARE


ie_sistema_externo_w    paciente_senha_fila.ie_sistema_externo%type;
nr_seq_mot_inut_w	motivo_inutilizacao_senha.nr_sequencia%type;
nr_subscription_id_w	paciente_senha_fila.nr_sequencia%type := nr_subscription_id_p;
nm_usuario_w		paciente_senha_fila.nm_usuario_inutilizacao%type := nm_usuario_p;
vl_ie_situacao_w    motivo_inutilizacao_senha.ie_situacao%type := 'A';


BEGIN

    <<CONSULTA_IE_SISTEMA_EXT>>
    begin
        select		ie_sistema_externo
        into STRICT		ie_sistema_externo_w
        from		paciente_senha_fila
        where		nr_sequencia = nr_subscription_id_w;
    exception
        when no_data_found or too_many_rows then
            ie_sistema_externo_w := null;
    end;

    <<CONSULTA_MOTIVO_INUT>>
    begin
        select		max(nr_sequencia)
        into STRICT		nr_seq_mot_inut_w
        from		motivo_inutilizacao_senha
        where   	ie_situacao = vl_ie_situacao_w;
    exception
        when no_data_found or too_many_rows then
            nr_seq_mot_inut_w := null;
    end;

    if (nr_subscription_id_w IS NOT NULL AND nr_subscription_id_w::text <> '') then
        update	paciente_senha_fila
        set 	dt_inutilizacao 		= clock_timestamp(),
            nr_seq_motivo_inutilizacao 	= nr_seq_mot_inut_w,
            dt_inicio_atendimento 		 = NULL,
            dt_fim_atendimento 		 = NULL,
            nm_usuario_inutilizacao		= nm_usuario_w,
            ds_maquina_inutilizacao		= wheb_usuario_pck.get_nm_maquina,
            ie_sistema_externo		 = NULL
        where	nr_subscription_id_p > 0 
        and	nr_sequencia = nr_subscription_id_w;
        commit;

        update    paciente_senha_fila
          set     ie_sistema_externo = ie_sistema_externo_w
          where   nr_sequencia = nr_subscription_id_w;
          commit;
    end if;

end;
$body$
