CREATE OR REPLACE PROCEDURE inventario_item_ambos ( nr_inventario_p bigint, cd_material_p bigint, cd_mat_estoque_p bigint, qt_diferenca_p bigint, qt_inventario_p bigint, cd_unidade_est_p text, nr_seq_lote_fornec_p bigint, cd_local_estoque_p bigint, dt_mesano_ref_p timestamp, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_operacao_saida_w		operacao_estoque.cd_operacao_estoque%type;
qt_material_estoque_w		movimento_estoque.qt_estoque%type;
qt_estoque_consig_w		movimento_estoque.qt_estoque%type;
qt_dif_estoque_w			movimento_estoque.qt_estoque%type;
nr_movimento_w			movimento_estoque.nr_movimento_estoque%type;
cd_cgc_fornec_ant_w		pessoa_juridica.cd_cgc%type;
cd_cgc_fornec_w			pessoa_juridica.cd_cgc%type;

ds_erro_w			varchar(2000);


BEGIN
cd_operacao_saida_w := sup_verifica_cria_operacao(cd_estabelecimento_p, 18, nm_usuario_p, cd_operacao_saida_w);

qt_material_estoque_w	:= qt_diferenca_p;
qt_dif_estoque_w		:= 0;
cd_cgc_fornec_ant_w	:= '0';

while(qt_material_estoque_w > 0) loop
	begin
	cd_cgc_fornec_w	:= substr(obter_fornecedor_regra_consig(cd_estabelecimento_p, cd_mat_estoque_p, '1', cd_local_estoque_p),1,14);
	qt_estoque_consig_w := obter_saldo_estoque_consignado(cd_estabelecimento_p, cd_cgc_fornec_w, cd_mat_estoque_p, cd_local_estoque_p, clock_timestamp(), qt_estoque_consig_w);

	if (qt_estoque_consig_w > 0) then
		if (qt_estoque_consig_w < qt_material_estoque_w) then
			qt_dif_estoque_w		:= qt_material_estoque_w - qt_estoque_consig_w;
			qt_material_estoque_w	:= qt_estoque_consig_w;
		end if;
		
		select	nextval('movimento_estoque_seq')
		into STRICT	nr_movimento_w
		;

		insert into movimento_estoque(
			nr_movimento_estoque,
			cd_estabelecimento,
			cd_local_estoque,
			dt_movimento_estoque,
			cd_operacao_estoque,
			cd_acao,
			cd_material,
			dt_mesano_referencia,
			qt_movimento,
			dt_atualizacao,
			nm_usuario,
			ie_origem_documento,
			ie_origem_proced,
			nr_documento,
			cd_unidade_medida_estoque,
			qt_estoque,
			dt_processo,
			cd_unidade_med_mov,
			cd_material_estoque,
			qt_inventario,
			cd_fornecedor,
			ie_movto_consignado,
			ds_observacao)
		values (nr_movimento_w,
			cd_estabelecimento_p,
			cd_local_estoque_p,
			clock_timestamp(),
			cd_operacao_saida_w,
			1,
			cd_material_p,
			dt_mesano_ref_p,
			qt_material_estoque_w,
			clock_timestamp(),
			nm_usuario_p,
			5,
			1,
			nr_inventario_p,
			cd_unidade_est_p,
			qt_material_estoque_w,
			null,
			cd_unidade_est_p,
			cd_mat_estoque_p,
			qt_inventario_p,
			cd_cgc_fornec_w,
			'C',
			ds_observacao_p);
			
		qt_material_estoque_w := qt_material_estoque_w - qt_estoque_consig_w;
		if (qt_dif_estoque_w > 0) then
			qt_material_estoque_w := qt_dif_estoque_w;
			qt_dif_estoque_w := 0;
		end if;
	else
		if (cd_cgc_fornec_ant_w <> cd_cgc_fornec_w) then
			cd_cgc_fornec_ant_w := cd_cgc_fornec_w;
		else
			exit;
		end if;
	end if;
	exception
		when others then
			ds_erro_w := substr(sqlerrm,1,2000);
	end;
end loop;

end;
$body$
