SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ipasgo_gerar_w_conv_ret_movto (nr_seq_retorno_p bigint) AS $body$
DECLARE


nr_doc_convenio_w	varchar(255);
cd_item_w		varchar(255);
ds_item_w		varchar(255);
qt_paga_w		double precision;
vl_pago_w		double precision;
cd_estabelecimento_w	bigint;
cd_convenio_w		bigint;
nr_interno_conta_w	bigint;
cd_glosa_conv_w		motivo_glosa.cd_motivo_glosa_conv%type;
cd_motivo_glosa_w	motivo_glosa.cd_motivo_glosa%type;
ds_complemento_w	convenio_Retorno_movto.ds_complemento%type;

c01 CURSOR FOR
SELECT	obter_valor_campo_separador(ds_conteudo,3,'|') cd_autorizacao,
	obter_valor_campo_separador(ds_conteudo,5,'|') cd_item,
	obter_valor_campo_separador(ds_conteudo,6,'|') ds_item,
	(replace(obter_valor_campo_separador(ds_conteudo,7,'|'),'.',','))::numeric  qt_paga,
	(replace(obter_valor_campo_separador(ds_conteudo,8,'|'),'.',','))::numeric  vl_pago,
	coalesce(somente_numero(replace(obter_valor_campo_separador(ds_conteudo,9,'|'),'.',',')),0) cd_glosa_conv
from	w_conv_ret_movto
where	nr_seq_retorno	= nr_seq_retorno_p
and	obter_valor_campo_separador(ds_conteudo,2,'|') in ('6','7','8')

union all

SELECT	obter_valor_campo_separador(ds_conteudo,3,'|') cd_autorizacao,
	obter_valor_campo_separador(ds_conteudo,6,'|') cd_item,
	obter_valor_campo_separador(ds_conteudo,7,'|') ds_item,
	(replace(obter_valor_campo_separador(ds_conteudo,8,'|'),'.',','))::numeric  qt_paga,
	(replace(obter_valor_campo_separador(ds_conteudo,10,'|'),'.',','))::numeric  vl_pago,
	0 cd_glosa_conv
from	w_conv_ret_movto
where	nr_seq_retorno	= nr_seq_retorno_p
and	obter_valor_campo_separador(ds_conteudo,2,'|') =  '4';


BEGIN

select	cd_estabelecimento,
	cd_convenio
into STRICT	cd_estabelecimento_w,
	cd_convenio_w
from	convenio_retorno
where	nr_sequencia	= nr_seq_retorno_p;

open C01;
loop
fetch C01 into
	nr_doc_convenio_w,
	cd_item_w,
	ds_item_w,
	qt_paga_w,
	vl_pago_w,
	cd_glosa_conv_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	max(a.nr_interno_conta)
	into STRICT	nr_interno_conta_w
	from	conta_paciente_guia b,
		conta_paciente a
	where	a.nr_interno_conta	= b.nr_interno_conta
	and	b.cd_autorizacao	= nr_doc_convenio_w
	and	a.cd_estabelecimento	= cd_estabelecimento_w
	and	a.cd_convenio_parametro	= cd_convenio_w;
	
	select 	max(cd_motivo_glosa)
	into STRICT	cd_motivo_glosa_w
	from	motivo_glosa
	where	cd_motivo_glosa_conv = cd_glosa_conv_w;
	
	ds_complemento_w := '';
	if	((coalesce(cd_motivo_glosa_w,0) = 0) and
		cd_glosa_conv_w > 0) then
		ds_complemento_w := 'Código ' || cd_glosa_conv_w || ' Não cadastrado';
	end if;
		
	insert into convenio_retorno_movto(nr_sequencia,
		nr_seq_retorno,
		nr_doc_convenio,		
		dt_atualizacao,
		nm_usuario,
		cd_item,
		ds_item_retorno,
		qt_paga,
		vl_pago,
		vl_total_pago,
		nr_conta,
		cd_motivo,
		ds_complemento)
	values (nextval('convenio_retorno_movto_seq'),
		nr_seq_retorno_p,
		nr_doc_convenio_w,
		clock_timestamp(),
		'TASY',
		cd_item_w,
		ds_item_w,
		qt_paga_w,
		vl_pago_w,
		vl_pago_w,
		nr_interno_conta_w,
		cd_motivo_glosa_w,
		ds_complemento_w);

end loop;
close C01;

delete	from w_conv_ret_movto
where	nr_seq_retorno	= nr_seq_retorno_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
