SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE iscv_acerto_duplic_pf ( CD_PESSOA_ORIGEM_P text, CD_PESSOA_DESTINO_P text) is x varchar(100) RETURNS boolean AS $body$
DECLARE

	qt_resultado_w	bigint;

	
BEGIN
	select	count(*) qt_resultado
	into STRICT	qt_resultado_w
	from 	regra_proc_interno_integra
	where	ie_tipo_integracao = 17; --Dominio criado para a integração
	return qt_resultado_w > 0;
	end;

	function permiteIntegrar(cd_pessoa_fisica_p varchar2) return boolean is	--Verificar se há algum procedimento interno que deva integrar
	ds_retorno_w		varchar2(1);
	begin
	if	permiteIntegrarISCV then
		nr_atendimento_w := obter_atendimento_paciente(cd_pessoa_fisica_p, obter_estabelecimento_ativo);

		select	coalesce(max(Obter_Se_Integr_Proc_Interno(pp.nr_seq_proc_interno, 17,null,pp.ie_lado,coalesce(pm.cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento))),'N') ie_permite_proc_integ
		into STRICT	ds_retorno_w
		from	prescr_procedimento pp,
				prescr_medica pm
		where	pm.nr_atendimento = nr_atendimento_w
		and		(pp.nr_seq_proc_interno IS NOT NULL AND pp.nr_seq_proc_interno::text <> '')
		and		pp.nr_sequencia = pm.nr_prescricao;

		if (ds_retorno_w = 'N') then

			select	coalesce(max(Obter_Se_Integr_Proc_Interno(nr_seq_proc_interno, 17,null,ie_lado,wheb_usuario_pck.get_cd_estabelecimento)),'N') ie_permite_proc_integ
			into STRICT	ds_retorno_w
			from	agenda_paciente
			where	(nr_seq_proc_interno IS NOT NULL AND nr_seq_proc_interno::text <> '')
			and		nr_atendimento = nr_atendimento_w;

		end if;

		return ds_retorno_w = 'S';
	else
		return false;
	end if;

	end;
begin

if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then
	if (permiteIntegrar(CD_PESSOA_ORIGEM_P))
	and (permiteIntegrar(CD_PESSOA_DESTINO_P)) then

		CALL gravar_agend_integracao(748, 'cd_pessoa_fisica=' || CD_PESSOA_DESTINO_P	|| ds_sep_bv_w ||
									 'cd_pessoa_origem=' || CD_PESSOA_ORIGEM_P	|| ds_sep_bv_w ||
									 'cd_estab_origem='  || obter_estabelecimento_ativo || ds_sep_bv_w, Obter_Setor_Atendimento(nr_atendimento_w));
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
