CREATE OR REPLACE PROCEDURE ish_close_reopen_case ( ish_caseno_p text, ie_close_reopen_p text) AS $body$
DECLARE


episodio_paciente_w	episodio_paciente%rowtype;

BEGIN

begin

begin
select	*
into STRICT	episodio_paciente_w
from	episodio_paciente
where	nr_episodio = ish_caseno_p;
exception
when others then
	episodio_paciente_w.nr_sequencia := null;
end;

if (episodio_paciente_w.nr_sequencia IS NOT NULL AND episodio_paciente_w.nr_sequencia::text <> '') then
	if (ie_close_reopen_p = 'C') and (coalesce(episodio_paciente_w.dt_fim_episodio::text, '') = '') then
		CALL encerrar_episodio(episodio_paciente_w.nr_sequencia);
	elsif (ie_close_reopen_p = 'R') and (episodio_paciente_w.dt_fim_episodio IS NOT NULL AND episodio_paciente_w.dt_fim_episodio::text <> '') then
		CALL reopen_case(episodio_paciente_w.nr_sequencia);
	end if;
end if;
exception
when others then
	null;
end;

end;
