CREATE OR REPLACE PROCEDURE ish_fault_processing ( nr_seq_fila_p bigint, xml_p xml, ie_status_p INOUT text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT	*
from	xmltable(
		xmlnamespaces(
		'http://schemas.xmlsoap.org/soap/envelope/' as "soapenv"),
	'soapenv:Envelope/soapenv:Body/soapenv:Fault' passing xml_p columns
	faultstring varchar(2000) path 'faultstring') a;

c01_w	c01%rowtype;

ds_log_w	varchar(4000);
fault_w		xml;


BEGIN
open c01;
loop
fetch c01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_status_p	:=	'E';

	if (length(ds_log_w) > 0) then
		intpd_gravar_log_recebimento(nr_seq_fila_p, c01_w.faultstring, 'INTPDTASY');
	end if;
	end;
end loop;
close c01;

end;
$body$
