CREATE OR REPLACE PROCEDURE ish_gerar_alta ( nr_atendimento_p bigint, cd_profissional_p text, ie_prob_alta_p text, dt_alta_p timestamp, nm_usuario_p text, ds_justificativa_p text default null, ie_previsao_p text DEFAULT NULL, ie_acao_p text DEFAULT NULL, cd_motivo_alta_p bigint DEFAULT NULL, ie_alta_estorno_p text DEFAULT NULL, ds_erro_p INOUT text DEFAULT NULL, cd_cgc_p text DEFAULT NULL, cd_processo_alta_p bigint default null, nr_seq_motivo_p bigint default null, ds_observacao_p text default null, cd_contract_hosp_p text default null, cd_contract_role_p text default null, cd_contract_type_p bigint default null, cd_cgc_transporte_p text default null, nr_seq_motivo_est_alta_p bigint default null) AS $body$
DECLARE


cd_medico_resp_w	varchar(50);


BEGIN
if (coalesce(ie_previsao_p, 'R') = 'P') then

	if (coalesce(cd_profissional_p::text, '') = '') then
		select	max(cd_medico_resp)
		into STRICT	cd_medico_resp_w
		from	atendimento_paciente
		where	nr_atendimento = nr_atendimento_p;
	end if;
	
	CALL gerar_atend_previsao_alta(	nr_atendimento_p,
		coalesce(cd_profissional_p,cd_medico_resp_w),
		dt_alta_p,
		ie_prob_alta_p,
		nm_usuario_p,
		ds_justificativa_p,
		ie_acao_p);
else
	if ((dt_alta_p IS NOT NULL AND dt_alta_p::text <> '') and ie_acao_p = 'A') then
		CALL atualizar_motivo_alta(nr_atendimento_p, cd_motivo_alta_p);
	else
		ds_erro_p := gerar_estornar_alta(	nr_atendimento_p, ie_alta_estorno_p, cd_processo_alta_p, cd_motivo_alta_p, dt_alta_p, nm_usuario_p, ds_erro_p, nr_seq_motivo_p, cd_cgc_p, ds_observacao_p, cd_contract_hosp_p, cd_contract_role_p, cd_contract_type_p, cd_cgc_transporte_p, nr_seq_motivo_est_alta_p);
	end if;

end if;

end;
$body$
