CREATE OR REPLACE PROCEDURE ish_send_case_insurance_atx ( nr_seq_episodio_p episodio_paciente.nr_sequencia%type, reg_integracao_p INOUT gerar_int_padrao.reg_integracao) AS $body$
DECLARE


