SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ja_aguardando_conf_medic_js ( cd_material_p bigint, qt_dispensar_p bigint, nr_seq_medic_item_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_exige_lote_w		varchar(1);
valida_w		varchar(1);
ds_erro_w		varchar(255);


BEGIN
	select 	max(coalesce(ie_exige_lote_fornecedor,'N'))
	into STRICT 	ie_exige_lote_w
	from	fa_medic_farmacia_amb
	where 	cd_material = cd_material_p;

	if (ie_exige_lote_w = 'S') and (qt_dispensar_p > 0) then
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT 	valida_w
		from	fa_entrega_medic_item_lote
		where	nr_seq_medic_item = nr_seq_medic_item_p;

		if (valida_w = 'N') then
			begin
				ds_erro_w := obter_texto_tasy(74342, wheb_usuario_pck.get_nr_seq_idioma);
				ds_erro_p := ds_erro_w;
			end;
		end if;
		end;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
