     CALL gerar_evento_paciente(nr_seq_evento_w, null, null, null, nm_usuario_w, null, null, 'S', ds_mensagem_p);
     CALL enviar_evento_paciente('S');
    end;
   end loop;
   close C01;
    
   commit;
  exception 
  when others then 
   null;
