SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE json_bloquear_projeto (nr_seq_projeto_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then

	if (ie_opcao_p = 'B') then
		update	json_projeto
		set		dt_bloqueio = clock_timestamp(),
				nm_usuario_bloqueio = nm_usuario_p,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_projeto_p;

	elsif (ie_opcao_p = 'D') then
		update	json_projeto
		set		dt_bloqueio  = NULL,
				nm_usuario_bloqueio  = NULL,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_projeto_p;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
