SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE jup_autorizacao_convenio (nr_sequencia_p bigint, nr_seq_estagio_p bigint) AS $body$
DECLARE



nm_usuario_w		varchar(15) := wheb_usuario_pck.Get_Nm_usuario;

BEGIN

Update	autorizacao_convenio a
set	a.nr_seq_estagio	= coalesce(nr_seq_estagio_p, a.nr_seq_estagio),
	a.nm_usuario	= nm_usuario_w,
	a.dt_Atualizacao 	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p
and exists ( SELECT * from estagio_autorizacao e
	where 	e.nr_sequencia 	= coalesce(nr_seq_estagio_p,0)
	and 	ie_situacao 	= 'A'
	and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = e.cd_empresa);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
