CREATE OR REPLACE PROCEDURE km_duplicar_questionario ( nr_seq_questionario_p km_questionario.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_questionario_w		km_questionario.nr_sequencia%type;
km_questionario_row_w		km_questionario%rowtype;
km_questionario_item_row_w	km_questionario_item%rowtype;


BEGIN

select	nextval('km_questionario_seq')
into STRICT	nr_seq_questionario_w
;

insert into km_questionario(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_questionario,
	ie_tipo_conhecimento,
	cd_funcao,
	nr_seq_habilidade,
	ie_area_utilizacao,
	ie_situacao)
SELECT	nr_seq_questionario_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	substr(obter_desc_expressao(735670) || ' ' || ds_questionario, 1, 2000),
	ie_tipo_conhecimento,
	cd_funcao,
	nr_seq_habilidade,
	ie_area_utilizacao,
	'A'
from	km_questionario
where	nr_sequencia = nr_seq_questionario_p;

insert into km_questionario_item(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_apres,
	ds_item,
	nr_seq_questionario,
	qt_peso_item,
	nr_seq_item_origem,
	cd_exp_item)
SELECT	nextval('km_questionario_item_seq'),
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	qi.nr_seq_apres,
	qi.ds_item,
	nr_seq_questionario_w,
	qi.qt_peso_item,
	qi.nr_seq_item_origem,
	qi.cd_exp_item
from	km_questionario_item qi
where	qi.nr_seq_questionario = nr_seq_questionario_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
