CREATE OR REPLACE PROCEDURE labweb_alterar_senha_paciente ( cd_pessoa_fisica_p text, ds_senha_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ds_senha_old_w		pessoa_fisica.ds_senha%type;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select	max(ds_senha)
	into STRICT	ds_senha_old_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ds_senha_old_w IS NOT NULL AND ds_senha_old_w::text <> '') then
		begin

		insert into pf_hist_senha(
			nr_sequencia,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_atualizacao,
			nm_usuario,
			cd_pessoa_fisica,
			ds_senha)
		values (
			nextval('pf_hist_senha_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_p,
			ds_senha_old_w);
		end;
	end if;

	update	pessoa_fisica
	set	ds_senha = ds_senha_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
