CREATE OR REPLACE PROCEDURE lab_altera_exame_grupo_exame ( nr_seq_exame_p bigint, nr_seq_superior_p bigint, nr_seq_grupo_p bigint, ie_exame_p text) AS $body$
BEGIN

if (ie_exame_p = 'S') and (nr_seq_superior_p IS NOT NULL AND nr_seq_superior_p::text <> '') then
	begin
	update 	exame_laboratorio
	set 	nr_seq_superior = nr_seq_superior_p
	where 	nr_seq_exame  = nr_seq_exame_p;
	end;

elsif (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then
	begin
	update 	exame_laboratorio
	set 	nr_seq_superior  = NULL,
		nr_seq_grupo   = nr_seq_grupo_p
	where 	nr_seq_exame = nr_seq_exame_p;
	end;
end if;

commit;

end;
$body$
