SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_altera_exame (nr_seq_superior_p bigint, nr_seq_grupo_p bigint, nr_seq_exame_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_exames_grupo_w	bigint;
nr_Seq_grupo_w		bigint;

BEGIN

if (coalesce(nr_seq_superior_p,0) > 0) then

	select 	max(nr_Seq_grupo)
	into STRICT	nr_Seq_grupo_w
	from	exame_laboratorio
	where	nr_seq_exame = nr_seq_superior_p;

end if;

update	exame_laboratorio
set 	nr_seq_superior = nr_seq_superior_p,
	nr_seq_grupo	= coalesce(nr_Seq_grupo_w,nr_seq_grupo_p),
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_seq_exame	= nr_seq_exame_p;

select 	count(*)
into STRICT	qt_exames_grupo_w
from 	exame_laboratorio
where	nr_seq_superior = nr_seq_exame_p
and	nr_seq_grupo 	<> nr_seq_grupo_p;

if (qt_exames_grupo_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(185151);
	--'Problemas ao troca o exame. Verifique os itens do exame foram movidos corretamente.
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
