SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_alt_status_env_col_prescr ( nr_prescricao_p bigint, nr_seq_prescr_proc_p bigint, ie_acao_p text, cd_equip_pda_p text, nm_usuario_p text, ie_status_atend_p text, ie_gerar_adep_p text) AS $body$
DECLARE

/* IE_ACAO_P 
* Verifica se está confirmando a coleta ou se está coletando os exames em contigência. 
* CONFIRM - Confirmando coleta 
* SEMETIQUETA - Coletando exames em contingência 
*/
 

BEGIN 
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ie_status_atend_p IS NOT NULL AND ie_status_atend_p::text <> '') and (nr_seq_prescr_proc_p IS NOT NULL AND nr_seq_prescr_proc_p::text <> '') then 
	begin 
	update	prescr_procedimento 
	set	ie_status_atend	= ie_status_atend_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		cd_equip_pda = cd_equip_pda_p 
	where	nr_sequencia	= nr_seq_prescr_proc_p 
	and	nr_prescricao	= nr_prescricao_p 
	and	((ie_status_atend = 16 
	and	ie_acao_p = 'CONFIRM') 
	or	ie_acao_p = 'SEMETIQUETA');
	 
	 
	update 	prescr_proc_controle_etiq 
	set	ie_status_atend = ie_status_atend_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_prescricao 	= nr_prescricao_p 
	and	ie_status_atend = 16 
	and	((ie_status_atend = 16 
	and	ie_acao_p = 'CONFIRM') 
	or	ie_acao_p = 'SEMETIQUETA');
	 
	CALL lab_atual_adminis_adep(nr_prescricao_p,nr_seq_prescr_proc_p,nm_usuario_p, ie_gerar_adep_p);
	 
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
