CREATE OR REPLACE PROCEDURE lab_atualizar_cond_amostra ( nr_prescricao_p text, nr_seq_material_p text, ds_condicao_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (ds_condicao_p IS NOT NULL AND ds_condicao_p::text <> '') then

	update 	prescr_proc_material
	set 	ds_condicao		= substr(ds_condicao_p, 1, 255),
	        nm_usuario 	 	= nm_usuario_p,
			dt_atualizacao  = clock_timestamp()
	where 	nr_prescricao 	= nr_prescricao_p
	and 	nr_seq_material = nr_seq_material_p;
commit;

end if;

end;
$body$
