SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_atualizar_dados_recoleta ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_acondicionamento_p bigint, nr_seq_motivo_recoleta_p bigint, nr_seq_frasco_p bigint, ds_avisado_p text, ds_solicitante_p text, ie_jejum_p text, qt_tempo_jejum_p text, nm_usuario_p text, ds_observacao_p text) AS $body$
DECLARE


nr_seq_material_w		material_exame_lab.nr_sequencia%type;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	select  MAX(b.nr_sequencia)
	into STRICT	nr_seq_material_w
	from    prescr_procedimento a,
			material_exame_lab  b
    where 	a.cd_material_exame  = b.cd_material_exame
	and		a.nr_prescricao = nr_prescricao_p
	and		a.nr_sequencia  = nr_seq_prescr_p;

	if ((nr_seq_motivo_recoleta_p IS NOT NULL AND nr_seq_motivo_recoleta_p::text <> '') and nr_seq_motivo_recoleta_p <> 0 ) then

		update 	prescr_procedimento
		set	   	nr_seq_recoleta = nr_seq_motivo_recoleta_p
		where 	nr_prescricao = nr_prescricao_p
		and		nr_sequencia  = nr_seq_prescr_p;

	end if;

	insert into	prescr_proc_recoleta(	nr_sequencia,
										nr_prescricao,
										nr_seq_prescr,
										nr_seq_material,
										dt_atualizacao_nrec,
										nm_usuario_nrec,
										dt_atualizacao,
										nm_usuario,
										nr_seq_motivo_recoleta,
										ie_jejum,
										qt_hora_jejum,
										nr_seq_acondicionamento,
										nr_seq_frasco,
										ds_avisado,
										ds_solicitante,
										ds_observacao)
					values ( nextval('prescr_proc_recoleta_seq'),
								  nr_prescricao_p,
								  nr_seq_prescr_p,
								  nr_seq_material_w,
								  clock_timestamp(),
								  nm_usuario_p,
								  clock_timestamp(),
								  nm_usuario_p,
								  CASE WHEN nr_seq_motivo_recoleta_p=0 THEN null  ELSE nr_seq_motivo_recoleta_p END ,
								  ie_jejum_p,
								  qt_tempo_jejum_p,
								  CASE WHEN nr_seq_acondicionamento_p=0 THEN null  ELSE nr_seq_acondicionamento_p END ,
								  CASE WHEN nr_seq_frasco_p=0 THEN null  ELSE nr_seq_frasco_p END ,
								  ds_avisado_p,
								  ds_solicitante_p,
								  ds_observacao_p);



end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
