SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_atualizar_status_matrix (nr_prescricao_p bigint, procedimentos_p text, ds_status_integracao_p text) AS $body$
DECLARE

  ie_status_alterar_w regra_exame_status.ie_status_regra%type;
  ie_executou_w varchar(1) := 'N';
  c RECORD;

BEGIN

begin
  select r.ie_status_regra
  into STRICT   ie_status_alterar_w
  from   regra_exame_status r
  where  r.ds_sigla_integracao = ds_status_integracao_p;
exception
  when too_many_rows then
       CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(1091669);

  when no_data_found then
       CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(1091670);
end;

if (ie_status_alterar_w IS NOT NULL AND ie_status_alterar_w::text <> '') then
   --Tentar realizar update por procedimento
   for c in (SELECT pp.nr_sequencia
             from   prescr_procedimento pp,
                    table(lista_pck.obter_lista(coalesce(procedimentos_p,'0'), ',')) l
           where  pp.nr_sequencia =  CASE WHEN coalesce(procedimentos_p,'0')='0' THEN  pp.nr_sequencia  ELSE l.nr_registro END
             and    pp.nr_prescricao = nr_prescricao_p) loop

     CALL matrix_alterar_status_ws(nr_prescricao_p, c.nr_sequencia,ie_status_alterar_w);
    ie_executou_w := 'S';
   end loop;
end if;

if (ie_executou_w = 'S') then
   commit;
else
   CALL WHEB_MENSAGEM_PCK.exibir_mensagem_abort(1097088);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
