CREATE OR REPLACE PROCEDURE lab_atualiza_acesso_gerenc_arq ( ds_maquina_p INOUT text, ds_usuario_loc_p INOUT text) AS $body$
BEGIN
	begin
	SELECT SUBSTR(MAX(machine),1,255),
		     substr(MAX(OSUSER),1,255)
	  into STRICT ds_maquina_p,
		     ds_usuario_loc_p
	  FROM gv$session
	 WHERE 1 = 1
	   AND audsid <> USERENV('sessionid')
	   AND UPPER(MODULE) LIKE '%CORLAB_FG%'  LIMIT 1;

	exception
	when others then
		ds_maquina_p := '';
		ds_usuario_loc_p:= '';
	end;
commit;

end;
