CREATE OR REPLACE PROCEDURE lab_atualiza_ds_pedido_externo ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ds_pedido_ext_p text, nm_usuario_p text, ds_infor_p INOUT text) AS $body$
DECLARE


ds_infor_w						varchar(2000) := '';
ds_pedido_ext_prescr_medica_w	prescr_medica.ds_pedido_ext%type;


BEGIN

if (ds_pedido_Ext_p IS NOT NULL AND ds_pedido_Ext_p::text <> '')  then

	begin
	update 	prescr_procedimento
	set	   	ds_pedido_ext_item = ds_pedido_ext_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where  	nr_prescricao = nr_prescricao_p
	and 	nr_sequencia = nr_seq_prescr_p;
	exception
	when others then
		ds_infor_w := Wheb_mensagem_pck.get_texto(308854) || ' ' /*'Problema ao atualizar o pedido externo item: '*/||substr(sqlerrm,1,1800)||chr(13)||chr(10);
	end;

	select  MAX(ds_pedido_ext)
	into STRICT	ds_pedido_ext_prescr_medica_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;


	if (coalesce(ds_pedido_ext_prescr_medica_w::text, '') = '')  then

		begin
		update 	prescr_medica
		set		ds_pedido_ext = ds_pedido_Ext_p,
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
		where 	nr_prescricao = nr_prescricao_p;
		exception
		when others then
			ds_infor_w := ds_infor_w||Wheb_mensagem_pck.get_texto(308855) || ' ' /*'Problema ao atualizar o pedido externo: '*/||substr(sqlerrm,1,1800);
		end;

	end if;

end if;

ds_infor_p := ds_infor_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
