CREATE OR REPLACE PROCEDURE lab_atualiza_local_atendimento (nr_prescricao_p bigint, nr_seq_local_pa_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
										  
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;


BEGIN 
 
select 	Max(nr_atendimento) 
into STRICT 	nr_atendimento_w 
from	prescr_medica 
where 	nr_prescricao = nr_prescricao_p;
 
if 	(nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '' AND nr_seq_local_pa_p IS NOT NULL AND nr_seq_local_pa_p::text <> '') then 
	 
	update atendimento_paciente 
	set 	nr_seq_local_pa   = nr_seq_local_pa_p, 
		dt_atualizacao 	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p 
	where	nr_atendimento = nr_atendimento_w;
 
end if;
 
commit;
 
end;
