SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_atualiza_regra_pacote_proc ( nr_seq_secretaria_p bigint, nr_seq_ficha_p bigint, nm_usuario_p text, dt_coleta_ficha_p timestamp, hr_coleta_p timestamp, dt_nascimento_p timestamp, hr_nascimento_p timestamp) AS $body$
DECLARE



nr_seq_pacote_w		   lote_ent_secretaria.nr_seq_pacote%type;
nr_seq_ficha_w		   lote_ent_sec_ficha.nr_sequencia%type;
nr_seq_exame_lab_w	 exame_laboratorio.nr_seq_exame%type;
cd_material_exame_w	 material_exame_lab.cd_material_exame%type;
ie_tipo_data_w	 	   lote_ent_tipo_coleta.ie_tipo_data%type;
qt_unidade_w		     lote_ent_tipo_coleta.qt_unidade%type;
qt_dif_coleta_w		   double precision;
qt_valor_abs_w		   double precision;
ie_precoce_w		     varchar(1);

C02 CURSOR FOR
	SELECT	substr(obter_cd_material_exame_lab(b.nr_seq_exame_lab),1,20),
			b.nr_seq_exame_lab
	from  	pacote_procedimento a,
			proc_interno b
	where	a.nr_seq_pacote = nr_seq_pacote_w
	and		a.nr_seq_proc_interno = b.nr_sequencia
	and		(b.nr_seq_exame_lab IS NOT NULL AND b.nr_seq_exame_lab::text <> '');


BEGIN

select	max(nr_seq_pacote)
into STRICT	nr_seq_pacote_w
from	lote_ent_secretaria
where	nr_sequencia = nr_seq_secretaria_p;

nr_seq_ficha_w := nr_seq_ficha_p;
qt_dif_coleta_w :=  round((to_date(to_char(coalesce(dt_coleta_ficha_p,clock_timestamp()),'dd/mm/yyyy')||' '||to_char(coalesce(hr_coleta_p,clock_timestamp()),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') - to_date(to_char(coalesce(dt_nascimento_p,clock_timestamp()),'dd/mm/yyyy')||' '||to_char(coalesce(hr_nascimento_p,clock_timestamp()),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'))::numeric,4);

open c02;
loop
fetch c02 into 	cd_material_exame_w,
				nr_seq_exame_lab_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	begin

	ie_precoce_w := '';

	select	max(a.qt_unidade),
			max(a.ie_tipo_data)
	into STRICT	qt_unidade_w,
			ie_tipo_data_w
	from	lote_ent_tipo_coleta a
	where	a.nr_seq_exame = nr_seq_exame_lab_w
	and		ie_tipo_coleta = 'P'
	and		coalesce(a.ie_situacao,'A') = 'A';

	if (ie_tipo_data_w IS NOT NULL AND ie_tipo_data_w::text <> '') then
		if (ie_tipo_data_w = 'H') then
			qt_valor_abs_w	:= qt_dif_coleta_w * 24;
		else
			qt_valor_abs_w	:= ROUND(qt_dif_coleta_w);
		end if;

		if (qt_valor_abs_w <= qt_unidade_w) then
			ie_precoce_w	:= 'P';
		end if;
	end if;

	select	max(a.qt_unidade),
			max(a.ie_tipo_data)
	into STRICT	qt_unidade_w,
			ie_tipo_data_w
	from	lote_ent_tipo_coleta a
	where	a.nr_seq_exame = nr_seq_exame_lab_w
	and		ie_tipo_coleta = 'T'
	and		coalesce(a.ie_situacao,'A') = 'A';

	if (ie_tipo_data_w IS NOT NULL AND ie_tipo_data_w::text <> '') then
		if (ie_tipo_data_w = 'H') then
			qt_valor_abs_w	:= qt_dif_coleta_w * 24;
		else
			qt_valor_abs_w	:= ROUND(qt_dif_coleta_w);
		end if;

		if (qt_valor_abs_w >= qt_unidade_w) then
			ie_precoce_w	:= 'T';
		end if;
	end if;

	CALL gravar_log_tasy(30,ie_precoce_w,'APAE');

	update  lote_ent_sec_ficha_exam
	set		ie_tipo_coleta = ie_precoce_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where   nr_seq_ficha = nr_seq_ficha_w
	and		nr_seq_exame = nr_seq_exame_lab_w
	and		cd_material_exame = cd_material_exame_w;

	end;
end loop;
close c02;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lab_atualiza_regra_pacote_proc ( nr_seq_secretaria_p bigint, nr_seq_ficha_p bigint, nm_usuario_p text, dt_coleta_ficha_p timestamp, hr_coleta_p timestamp, dt_nascimento_p timestamp, hr_nascimento_p timestamp) FROM PUBLIC;

