SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_atual_dt_emissao_req_lab (nr_prescricao_p text, nm_usuario_p text) AS $body$
DECLARE



dt_entrega_anterior_w		timestamp;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select  MAX(dt_entrega)
	into STRICT	dt_entrega_anterior_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p
	and (coalesce(dt_entrega, dt_prescricao) < dt_prescricao);

	if (dt_entrega_anterior_w IS NOT NULL AND dt_entrega_anterior_w::text <> '') then

		update 	prescr_medica
		set		dt_entrega = clock_timestamp(),
				dt_atualizacao = clock_timestamp(),
				nm_usuario = nm_usuario_p
		where 	nr_prescricao = nr_prescricao_p;

		CALL gravar_log_lab(32,' '||obter_desc_expressao(779476)||' '||dt_entrega_anterior_w,nm_usuario_p,nr_prescricao_p);
		commit;

	end if;

	update 	prescr_medica
	set	dt_emissao_req_lab = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where 	nr_prescricao = nr_prescricao_p;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
