SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_atual_seq_atual_equip ( ds_sigla_p text, nm_usuario_p text, nr_prescricao_p bigint default null) AS $body$
DECLARE



cd_equipamento_w	integer;
nr_seq_inicial_w	bigint;
nr_seq_atual_w		bigint;
nr_seq_final_w		bigint;


BEGIN

if (ds_sigla_p IS NOT NULL AND ds_sigla_p::text <> '') then

	select 	MAX(cd_equipamento),
		MAX(nr_seq_inicial),
		MAX(nr_seq_atual),
		MAX(nr_seq_final)
	into STRICT 	cd_equipamento_w,
		nr_seq_inicial_w,
		nr_seq_atual_w,
		nr_seq_final_w
	from 	equipamento_lab
	where 	trim(both upper(ds_sigla)) = trim(both upper(ds_sigla_p));

		if (cd_equipamento_w IS NOT NULL AND cd_equipamento_w::text <> '') then

			if (nr_seq_inicial_w > 0) 	and (nr_seq_atual_w > 0) 	and
				((nr_seq_atual_w + 1) > nr_seq_final_w)	then
					nr_seq_atual_w := nr_seq_inicial_w;
			end if;

			if (coalesce(nr_seq_atual_w::text, '') = '') then
			    nr_seq_atual_w := nr_seq_inicial_w + 1;
			else
			    nr_seq_atual_w := nr_seq_atual_w + 1;
			end if;

			update	equipamento_lab
			set	nr_seq_atual   = nr_seq_atual_w,
				nm_usuario     = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where 	cd_equipamento = cd_equipamento_w;
			commit;

			update prescr_medica
			set	   nr_controle_int_lab = nr_seq_atual_w
			where  nr_prescricao = nr_prescricao_p;

		end if;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
