CREATE OR REPLACE PROCEDURE lab_consistir_depend_conta ( nr_prescricao_p bigint, nr_seq_superior_p bigint, nr_seq_exame_p bigint, ie_gera_conta_p INOUT text) AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

BEGIN
	v_query := 'SELECT * FROM lab_consistir_depend_conta_atx ( ' || quote_nullable(nr_prescricao_p) || ',' || quote_nullable(nr_seq_superior_p) || ',' || quote_nullable(nr_seq_exame_p) || ',' || quote_nullable(ie_gera_conta_p) || ' )';
