CREATE OR REPLACE PROCEDURE lab_consistir_depend_conta_atx ( nr_prescricao_p bigint, nr_seq_superior_p bigint, nr_seq_exame_p bigint, ie_gera_conta_p INOUT text) AS $body$
DECLARE
nr_seq_exame_sup_w	prescR_procedimento.nr_seq_exame%type;


BEGIN

--Item pai
select	max(a.nr_seq_exame)
into STRICT	nr_seq_exame_sup_w
from	prescr_procedimento a
where	a.nr_prescricao = nr_prescricao_p
and		a.nr_sequencia = nr_seq_superior_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_gera_conta_p
from	exame_lab_dependente a
where	a.nr_seq_exame = nr_seq_exame_sup_w
and		a.nr_seq_exame_dep = nr_seq_exame_p
and		coalesce(a.ie_agrupa_conta,'S') = 'S';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
