CREATE OR REPLACE PROCEDURE lab_copiar_antib_material (cd_microorganismo_origem_p bigint, nr_seq_material_origem_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_medicamento_w	bigint;
nr_seq_material_w	bigint;
nr_seq_grupo_micro_w	bigint;
qt_itens_w		bigint;

C01 CURSOR FOR

SELECT	cd_medicamento,
	nr_seq_grupo_micro
from	cih_microorg_medic
where	cd_microorganismo = cd_microorganismo_origem_p
  and   NR_SEQ_MATERIAL = nr_seq_material_origem_p;

c02 CURSOR FOR
	SELECT	nr_sequencia
	from	MATERIAL_EXAME_LAB
	where	nr_sequencia <> nr_seq_material_origem_p
	and		coalesce(IE_APRES_MICROORG,'S') = 'S';


BEGIN


OPEN C01;
LOOP
FETCH C01 into
	cd_medicamento_w,
	nr_seq_grupo_micro_w;

EXIT WHEN NOT FOUND; /* apply on C01 */
	begin


	open C02;
	loop
	fetch C02 into
		nr_seq_material_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		select count(*)
		into STRICT	qt_itens_w
		from	cih_microorg_medic
		where	cd_microorganismo 	= cd_microorganismo_origem_p
		and	nr_seq_material		= nr_seq_material_w
		and	cd_medicamento		= cd_medicamento_w;

		if (qt_itens_w = 0) then
			insert into cih_microorg_medic(
				cd_microorganismo,
				nr_seq_material,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nm_usuario,
				cd_medicamento,
				nr_seq_grupo_micro)
			values (cd_microorganismo_origem_p,
				nr_seq_material_w,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p,
				cd_medicamento_w,
				nr_seq_grupo_micro_w);
		end if;


		end;
	end loop;
	close C02;




	end;
END LOOP;
CLOSE C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
