CREATE OR REPLACE PROCEDURE lab_desbloquear_exame_lote (nr_seq_lote_p bigint, nm_usuario_p text, ds_info_p INOUT text) AS $body$
DECLARE





C01 CURSOR FOR
	SELECT	distinct
		b.nr_prescricao,
		c.nr_sequencia nr_seq_prescr,
		c.nr_seq_exame,
		b.nr_sequencia nr_seq_ficha,
		substr(obter_desc_exame(c.nr_seq_exame),1,50) nm_exame
	from	lote_ent_secretaria a,
		lote_ent_sec_ficha b,
		prescr_procedimento c,
		lote_ent_sec_ficha_exam d
	where	a.nr_sequencia = b.nr_seq_lote_sec
	and	b.nr_prescricao = c.nr_prescricao
	and	b.nr_sequencia  = d.nr_seq_ficha
	and	d.nr_seq_exame  = c.nr_seq_exame
	and 	c.ie_exame_bloqueado = 'S'
	and	a.nr_sequencia = nr_seq_lote_p
	order by 1;

c01_w	c01%rowtype;

ds_info_w		varchar(200);


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and (nr_seq_lote_p > 0) then


open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin


	update  prescr_procedimento
	set	ie_exame_bloqueado = 'N',
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		nr_seq_motivo_bloqueio  = NULL
	where 	nr_prescricao = c01_w.nr_prescricao
	and	nr_sequencia  = c01_w.nr_seq_prescr;

	CALL gravar_log_lab(46,substr(WHEB_MENSAGEM_PCK.get_texto(402856,'NR_LOTE_SEC='||nr_seq_lote_p||';NR_SEQ_FICHA='||c01_w.nr_seq_ficha||';NR_PRESCRICAO='||c01_w.nr_prescricao||';NR_SEQ_EXAME='||c01_w.nr_seq_exame),1,2000),nm_usuario_p,c01_w.nr_prescricao);

	commit;

	end;
end loop;
close C01;

ds_info_p := substr(WHEB_MENSAGEM_PCK.get_texto(403141,'NR_SEQ_LOTE='||nr_seq_lote_p),1,200);

end if;

commit;




end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
