SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_desfazer_reconvocacao (nr_seq_lote_p bigint, nr_seq_ficha_p bigint, ds_seq_ficha_exam_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_ficha_w		lote_ent_sec_ficha.nr_sequencia%type;
nr_seq_reconvocado_w	lote_ent_reconvocado.nr_sequencia%type;
qt_reconvocado_w	bigint;



C01 CURSOR FOR
	SELECT	b.nr_sequencia, c.nr_sequencia nr_seq_reconvocado, e.nr_seq_exame
	from	lote_ent_secretaria a,
		lote_ent_sec_ficha  b,
		lote_ent_reconvocado c,
		lote_ent_reconvocado_item d,
		lote_ent_sec_ficha_exam e
	where	1 = 1
	and	a.nr_sequencia  	= b.nr_seq_lote_sec
	and	c.nr_seq_ficha_lote  	= b.nr_sequencia
	and	c.nr_seq_lote_sec  	= a.nr_sequencia
	and	b.nr_sequencia  	= e.nr_seq_ficha
	and	d.nr_seq_reconvocado    = c.nr_sequencia
	and	d.nr_seq_exame 		= e.nr_seq_exame
	and	coalesce(c.dt_suspensao_rec::text, '') = ''
	and	coalesce(nr_seq_ficha_p,0) 	= 0
	and	a.nr_sequencia  	= nr_seq_lote_p
	and	obter_se_contido(e.nr_sequencia, ds_seq_ficha_exam_p) = 'S'
	
union

	SELECT	b.nr_sequencia, c.nr_sequencia nr_seq_reconvocado, e.nr_seq_exame
	from	lote_ent_secretaria a,
		lote_ent_sec_ficha  b,
		lote_ent_reconvocado c,
		lote_ent_reconvocado_item d,
		lote_ent_sec_ficha_exam e
	where	1 = 1
	and	a.nr_sequencia  	= b.nr_seq_lote_sec
	and	c.nr_seq_ficha_lote  	= b.nr_sequencia
	and	c.nr_seq_lote_sec  	= a.nr_sequencia
	and	b.nr_sequencia  	= e.nr_seq_ficha
	and	d.nr_seq_reconvocado    = c.nr_sequencia
	and	d.nr_seq_exame 		= e.nr_seq_exame
	and	coalesce(c.dt_suspensao_rec::text, '') = ''
	and	coalesce(nr_seq_lote_p,0)	= 0
	and	b.nr_sequencia  	= nr_seq_ficha_p
	and	obter_se_contido(e.nr_sequencia, ds_seq_ficha_exam_p) = 'S'
	order by 1;

c01_w	C01%rowtype;



BEGIN

if (nr_seq_lote_p > 0) or (nr_seq_ficha_p > 0) then

   open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		delete FROM lote_ent_reconvocado_item
		where  nr_seq_reconvocado = c01_w.nr_seq_reconvocado
		  and  nr_seq_exame = c01_w.nr_seq_exame;

		select	coalesce(count(*), 0)
		into STRICT	qt_reconvocado_w
		from	lote_ent_reconvocado_item
		where	nr_seq_reconvocado = c01_w.nr_seq_reconvocado;

		if qt_reconvocado_w = 0 then

			delete FROM lote_ent_reconvocado
			where nr_sequencia = c01_w.nr_seq_reconvocado;

			--alterando o status da ficha novamente para normal
			update	lote_ent_sec_ficha
			set	ie_tipo_ficha = 'N',
				dt_atualizacao = clock_timestamp(),
				nm_usuario     = nm_usuario_p
			where	nr_sequencia = c01_w.nr_sequencia;

		end if;

		CALL gravar_log_lab(44,substr(WHEB_MENSAGEM_PCK.get_texto(402859,'NR_SEQ_FICHA='||c01_w.nr_sequencia||';NM_USUARIO='||nm_usuario_p||';DT_ALTERACAO='||clock_timestamp()||';CD_FUNCAO='||obter_funcao_ativa||';NR_PERFIL='||obter_perfil_ativo||';NR_SEQ_RECONVOCADO='||c01_w.nr_seq_reconvocado),1,2000),nm_usuario_p,c01_w.nr_sequencia);
		commit;
		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lab_desfazer_reconvocacao (nr_seq_lote_p bigint, nr_seq_ficha_p bigint, ds_seq_ficha_exam_p text, nm_usuario_p text) FROM PUBLIC;

