CREATE OR REPLACE PROCEDURE lab_email_comunicacao_critico ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, nm_pessoa_contato_p text, ds_obs_p text, nm_usuario_p text ) AS $body$
DECLARE

    nr_sequencia_w   lab_comunicacao_critico.nr_sequencia%TYPE;

BEGIN
    IF (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') THEN

        select nextval('lab_comunicacao_critico_seq')
          into STRICT nr_sequencia_w
;

        INSERT INTO lab_comunicacao_critico(
            nr_sequencia,
            nr_prescricao,
            nr_seq_prescricao,
            nm_pessoa_contato,
            ie_forma_comunicacao,
            ie_comunicacao_critico,
            ds_obs,
            dt_atualizacao,
            nm_usuario,
            nm_usuario_nrec,
            dt_atualizacao_nrec
        ) VALUES (
            nr_sequencia_w,
            nr_prescricao_p,
            nr_seq_prescricao_p,
            nm_pessoa_contato_p,
            1,
            3,
            ds_obs_p,
            clock_timestamp(),
            nm_usuario_p,
            nm_usuario_p,
            clock_timestamp()
        );
      COMMIT;
      CALL lab_liberar_tent_comunic(nr_prescricao_p, nr_seq_prescricao_p, nr_sequencia_w, 3, nm_usuario_p);
    END IF;
END;
$body$
