CREATE OR REPLACE PROCEDURE lab_fechar_lote_exame (nr_seq_lote_p bigint, ie_etapa_p bigint, ie_lote_p text, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_Seq_prescr_w		integer;

C01 CURSOR FOR
	SELECT 	a.nr_Seq_prescr,
		a.nr_prescricao
	from	lab_lote_exame_item a,
		lab_lote_exame b
	where 	b.nr_sequencia = a.nr_seq_lote
	and	a.nr_seq_lote = nr_seq_lote_p;


BEGIN

update lab_lote_exame
set ie_status = 'F'
where nr_sequencia = nr_seq_lote_p;

if ((coalesce(ie_etapa_p,0) > 0) and (ie_lote_p = 'I')) then

	OPEN C01;
	LOOP
	FETCH C01 into
		nr_Seq_prescr_w,
		nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		update	prescr_procedimento
		set 	ie_status_atend = ie_etapa_p
		where	nr_prescricao = nr_prescricao_w
		and	nr_Sequencia = nr_Seq_prescr_w
		and	ie_status_atend >= 35;

	END LOOP;
	CLOSE C01;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
