CREATE OR REPLACE PROCEDURE lab_finalizar_impressao_mapa ( cd_mapa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_imp_mapa_w		bigint;
nr_prescricao_w			bigint;
nr_seq_prescr_w			bigint;
status_prescr_atendida_w	smallint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_prescricao,
		nr_seq_prescr
	from	lab_impressao_mapa
	where	cd_mapa = cd_mapa_p;


BEGIN

--select	obter_parametro_funcao(10209, 5, nm_usuario_p)
--into	status_prescr_atendida_w
--from	dual;
select	Obter_Valor_Param_Usuario(10209, 5, Obter_perfil_Ativo, nm_usuario_p, obter_estabelecimento_ativo)
into STRICT	status_prescr_atendida_w
;

open C01;
loop
fetch C01 into
	nr_seq_imp_mapa_w,
	nr_prescricao_w,
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		CALL atualizar_lab_prescr_proc_imp(nr_prescricao_w, nr_seq_prescr_w, 'M', nm_usuario_p, 'I');

		CALL lab_insere_impressao_mapa_log(nr_prescricao_w, nr_seq_prescr_w, 30, cd_mapa_p, nm_usuario_p);

		if (status_prescr_atendida_w > 0) then

			update	prescr_procedimento
			set		ie_status_atend = status_prescr_atendida_w,
					dt_atualizacao = clock_timestamp(),
					nm_usuario = nm_usuario_p
			where	nr_prescricao = nr_prescricao_w
			and		nr_sequencia = nr_seq_prescr_w
			and 	status_prescr_atendida_w > ie_status_atend;

		end if;

		CALL lab_insere_impressao_mapa_log(nr_prescricao_w, nr_seq_prescr_w, 40, cd_mapa_p, nm_usuario_p);

		update	lab_impressao_mapa
		set	ie_status = 30
		where	nr_sequencia = nr_seq_imp_mapa_w;

		CALL lab_insere_impressao_mapa_log(nr_prescricao_w, nr_seq_prescr_w, 50, cd_mapa_p, nm_usuario_p);

	end;
end loop;
close C01;

commit;

end;
$body$
