CREATE OR REPLACE PROCEDURE lab_gerar_dt_result_atraso ( nr_prescricao_p prescr_procedimento.nr_prescricao%type, nr_seq_prescr_p prescr_procedimento.nr_sequencia%type, dt_result_atraso_p timestamp, nm_usuario_p text ) AS $body$
DECLARE

nr_seq_proc_compl_w	prescr_procedimento.nr_seq_proc_compl%type;

BEGIN

	select	nr_seq_proc_compl
	into STRICT	nr_seq_proc_compl_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and 	nr_sequencia = nr_seq_prescr_p;

	if (coalesce(nr_seq_proc_compl_w::text, '') = '') then

		select nextval('prescr_procedimento_compl_seq')
		into STRICT nr_seq_proc_compl_w
		;

		insert into prescr_procedimento_compl(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_result_atrasado
		)
		values (
		nr_seq_proc_compl_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		dt_result_atraso_p
		);

		update 	prescr_procedimento
		set 	nr_seq_proc_compl = nr_seq_proc_compl_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_prescricao = nr_prescricao_p
		and 	nr_sequencia = nr_seq_prescr_p;

	else

		update prescr_procedimento_compl
		set 	dt_result_atrasado = dt_result_atraso_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_proc_compl_w;

	end if;

commit;

end;
$body$
