SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_gerar_etiqueta_prescr_data (dt_fim_prescr_p timestamp, ie_acm_p text, ie_urgencia_p text, qt_exames_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_seq_grupo_w		bigint;
nr_seq_exame_w		bigint;
qt_exames_w		bigint;
nr_seq_material_w	bigint;
nr_etiqueta_w		bigint;
cd_exame_w		varchar(20);
qt_exame_w		smallint;
nr_sequencia_w		bigint;
nr_seq_lab_w		bigint;
ds_material_especial_w	varchar(255);
dt_prev_execucao_w	timestamp;
cd_exame_integracao_w	varchar(20);
ds_exame_amostra_w	varchar(20);
nr_seq_origem_w		integer;
nr_Seq_prescr_hor_w	integer;
nr_seq_prescr_proc_mat_w bigint;
cd_cgc_externo_w	varchar(14);
ds_prescricao_w 	varchar(500);
nr_prescricao_param_w	bigint;
nr_seq_prescricao_w	varchar(500);

c01 CURSOR FOR 
SELECT	a.nr_prescricao 
from	exame_Laboratorio c, 
	prescr_Medica b, 
	prescr_procedimento a, 
	atendimento_paciente d 
where	a.nr_prescricao	= b.nr_prescricao 
and	b.nr_atendimento	= d.nr_atendimento 
and	a.nr_seq_exame	= c.nr_seq_exame 
and	(cd_material_exame IS NOT NULL AND cd_material_exame::text <> '') 
and	ie_status_atend	= 10 
and	((substr(CASE WHEN coalesce(a.ie_acm,'N')='N' THEN  obter_se_intervalo_acm(a.cd_intervalo)  ELSE a.ie_acm END ,1,5) = ie_acm_p) or (ie_acm_p = 'N')) 
and	((a.ie_urgencia	= ie_urgencia_p) or (ie_urgencia_p = 'N')) 
and	trunc(dt_prev_execucao,'dd')	<= dt_fim_prescr_p 
and	a.cd_motivo_baixa	= 0 
and	d.IE_TIPO_ATENDIMENTO = 1 
and	a.ie_suspenso 	<> 'S' 
order by 1;

c02 CURSOR FOR 
SELECT	a.nr_sequencia 
from	exame_Laboratorio c, 
	prescr_Medica b, 
	prescr_procedimento a, 
	atendimento_paciente d 
where	a.nr_prescricao	= b.nr_prescricao 
and	b.nr_atendimento	= d.nr_atendimento 
and	a.nr_seq_exame	= c.nr_seq_exame 
and	(cd_material_exame IS NOT NULL AND cd_material_exame::text <> '') 
and	ie_status_atend	= 10 
and	((substr(CASE WHEN coalesce(a.ie_acm,'N')='N' THEN  obter_se_intervalo_acm(a.cd_intervalo)  ELSE a.ie_acm END ,1,5) = ie_acm_p) or (ie_acm_p = 'N')) 
and	((a.ie_urgencia	= ie_urgencia_p) or (ie_urgencia_p = 'N')) 
and	trunc(dt_prev_execucao,'dd')	<= dt_fim_prescr_p 
and	a.cd_motivo_baixa	= 0 
and	d.IE_TIPO_ATENDIMENTO = 1 
and	a.ie_suspenso 	<> 'S' 
and	a.nr_prescricao = nr_prescricao_param_w 
order by 1;


BEGIN 
 
DELETE FROM w_lab_etiqueta 
WHERE nm_usuario = nm_usuario_p;
 
open c01;
loop 
fetch c01 into 
	nr_prescricao_param_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_prescricao_w := '';
	open c02;
	loop 
	fetch c02 into 
		nr_seq_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		ds_prescricao_w := ds_prescricao_w || ',' || nr_seq_prescricao_w;
	end loop;
	close c02;
	CALL LAB_Gerar_Etiqueta(nr_prescricao_param_w, ds_prescricao_w, qt_exames_p, ie_opcao_p, nm_usuario_p);	
end loop;
close c01;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
