SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_gerar_nr_controle_exame ( nr_prescricao_p bigint, cd_estabelecimento_p bigint, ie_opcao_p bigint, ie_prescr_control_dup_p INOUT text) AS $body$
DECLARE


cd_estabelecimento_w		varchar(4);
cd_seq_estab_w				varchar(15);
cd_codigo_estab_w			varchar(4);
nr_seq_prescr_w				varchar(6);
nr_seq_prescr_ww			varchar(6);
nr_seq_superior_ww			varchar(6);
nr_ordem_amostra_w			bigint;
nr_controle_exame_w			varchar(20);
nr_sequencia_w				bigint;
qt_maximo_w					bigint;
qt_atual_w					bigint;
nr_seq_prescricao_w			varchar(6);
nr_seq_prescr_proc_mat_w	bigint;
nr_seq_int_prescr_w			bigint;
nr_seq_int_prescr_ww		varchar(10);
cd_equipamento_w			integer;
cd_equipamento_ww			integer;
nr_grupo_w					varchar(2);
nr_prescricao_ww			varchar(14);
nr_sequencia_ww				bigint;
ie_opcao_w					smallint;
qt_existe_controle_w		bigint;
ie_prescr_control_dup_w		varchar(1);
ie_existe_nr_seq_exame_w 	varchar(1);
ie_retorno_w				char(1);
nr_seq_zenite_w				varchar(15);
nr_control_pais_w			smallint;
ie_gera_controle			varchar(1);

/*ie_opcao_p
1 - balague
2 - Sérgio Franco */
C01 CURSOR FOR
SELECT	a.nr_sequencia
FROM 	prescr_procedimento a
WHERE	a.nr_prescricao	= nr_prescricao_p
AND		(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
and		coalesce(a.nr_controle_exame::text, '') = ''
ORDER BY 1;


BEGIN

ie_opcao_w := 0;
cd_equipamento_w := 0;
cd_equipamento_ww := 0;
ie_prescr_control_dup_w := 'N';
ie_gera_controle := 'N';

select	max(nr_sequencia),
		coalesce(max(NR_SEQ_INTERF_EQUIP),0),
		max(NR_CONTROL_DIV_PROCED),
		LPAD(max(NR_CONTROL_PROCED),3,0),
		coalesce(max(NR_CONTROL_PAIS),5)
into STRICT	nr_sequencia_w,
		cd_equipamento_ww,
		cd_estabelecimento_w,
		cd_codigo_estab_w,
		nr_control_pais_w
from	regra_controle_prescr
where	cd_estabelecimento = cd_estabelecimento_p
and		coalesce(ie_integracao,'N') = 'S';


select 	coalesce(max('S'),'N')
into STRICT	ie_existe_nr_seq_exame_w
from 	prescr_procedimento where		(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
and		coalesce(nr_controle_exame::text, '') = ''
and		nr_prescricao = nr_prescricao_p LIMIT 1;

if (cd_equipamento_ww = 2450) then
	ie_opcao_w := 4;
end if;

if (ie_opcao_p = 2) and (ie_opcao_w <> 4) then

	select 	MAX(cd_equipamento)
	into STRICT 	cd_equipamento_w
	from 	equipamento_lab
	where 	trim(both upper(ds_sigla)) = trim(both upper('LSFRANCO'));

end if;

if (ie_opcao_p = 1) and (ie_opcao_w <> 4) then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_ww
	from	regra_controle_prescr
	where	cd_estabelecimento = cd_estabelecimento_p
	and		nr_seq_interf_equip = 2212
	and		coalesce(ie_integracao,'N') = 'S';

	if (nr_sequencia_ww IS NOT NULL AND nr_sequencia_ww::text <> '') then
		ie_opcao_w := 3;
	end if;


end if;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

	if 	(ie_opcao_w = 0 AND ie_opcao_p = 1) then
		if (ie_existe_nr_seq_exame_w = 'S') then

			if (coalesce(cd_estabelecimento_w::text, '') = '') then
				select 	CASE WHEN cd_estabelecimento_p=33 THEN '6' WHEN cd_estabelecimento_p=1 THEN '1' WHEN cd_estabelecimento_p=11 THEN '2' WHEN cd_estabelecimento_p=71 THEN '3' WHEN cd_estabelecimento_p=81 THEN '4' WHEN cd_estabelecimento_p=91 THEN '5' WHEN cd_estabelecimento_p=41 THEN '7' WHEN cd_estabelecimento_p=300 THEN '8' END
				into STRICT 	cd_estabelecimento_w
				;
			end if;

			select 	LPAD((max(qt_atual) + 1),5,0),
					max(qt_maximo),
					max(qt_atual) + 1
			into STRICT	cd_seq_estab_w,
					qt_maximo_w,
					qt_atual_w
			from	REGRA_CONTROLE_PRESCR
			where	cd_estabelecimento = cd_estabelecimento_p
			and		nr_sequencia = nr_sequencia_w;

			if (qt_maximo_w > 0) 		and (qt_atual_w > qt_maximo_w) 	then
				qt_atual_w	:= 1;
			end if;

			if (coalesce(cd_codigo_estab_w::text, '') = '') then
				select 	LPAD('1',3,0)
				into STRICT 	cd_codigo_estab_w
				;
			end if;

			update	regra_controle_prescr
			set	qt_atual = qt_atual_w
			where	nr_sequencia = nr_sequencia_w;

		end if;
	elsif (ie_opcao_w = 4) then

		select 	LPAD((max(qt_atual) + 1),6,0),
				max(qt_maximo),
				max(qt_atual) + 1
		into STRICT	cd_seq_estab_w,
				qt_maximo_w,
				qt_atual_w
		from	REGRA_CONTROLE_PRESCR
		where	cd_estabelecimento = cd_estabelecimento_p
		and		nr_sequencia = nr_sequencia_w;

		nr_seq_zenite_w := '5'||cd_seq_estab_w||'002';

		update	regra_controle_prescr
		set	qt_atual = qt_atual_w
		where	nr_sequencia = nr_sequencia_w;

	end if;


	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

	OPEN C01;
	LOOP
	FETCH C01 into nr_seq_prescr_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		if 	(ie_opcao_w = 0 AND ie_opcao_p = 1) then
			select	max(nr_seq_prescr_proc_mat)
			into STRICT	nr_seq_prescr_proc_mat_w
			from	prescr_proc_mat_item
			where	nr_prescricao = nr_prescricao_p
			and		nr_seq_prescr = nr_seq_prescr_w;

			select	max(substr(nr_seq_int_prescr,1,2))
			into STRICT	nr_seq_int_prescr_w
			from	prescr_proc_material
			where	nr_sequencia = nr_seq_prescr_proc_mat_w;

			select	LPAD(coalesce(nr_seq_int_prescr_w,00),2,0)
			into STRICT	nr_seq_int_prescr_ww
			;


			nr_controle_exame_w := nr_control_pais_w||cd_estabelecimento_w||cd_seq_estab_w||cd_codigo_estab_w||nr_seq_int_prescr_ww;

		elsif (ie_opcao_w = 3) then

			select	max(nr_seq_prescr_proc_mat)
			into STRICT	nr_seq_prescr_proc_mat_w
			from	prescr_proc_mat_item
			where	nr_prescricao = nr_prescricao_p
			and	nr_seq_prescr = nr_seq_prescr_w;

			select	max(substr(nr_seq_int_prescr,1,2))
			into STRICT	nr_seq_int_prescr_w
			from	prescr_proc_material
			where	nr_sequencia = nr_seq_prescr_proc_mat_w;

			select	LPAD(coalesce(nr_seq_int_prescr_w,00),2,0)
			into STRICT	nr_grupo_w
			;

			if (length(nr_prescricao_p) >= 7) then
				nr_prescricao_ww := substr(to_char(nr_prescricao_p,'FM0000000'),1,7);--Tratamento para consistência da prescrição, quando possuir 7 ou mais digitos
				nr_controle_exame_w := '0'||nr_prescricao_ww||nr_grupo_w;
			else
				nr_prescricao_ww := lpad(substr(nr_prescricao_p,1,6),6,0);
				nr_controle_exame_w := '01'||nr_prescricao_ww||nr_grupo_w;

			end if;

		elsif (ie_opcao_w = 4) then

			select	MAX(b.nr_sequencia),
					MAX(c.nr_ordem_amostra),
					MAX(b.nr_seq_superior)
			into STRICT	nr_seq_prescr_ww,
					nr_ordem_amostra_w,
					nr_seq_superior_ww
			from	prescr_proc_mat_item a,
					prescr_procedimento b,
					exame_laboratorio c
			where	a.nr_prescricao = b.nr_prescricao
			and		a.nr_seq_prescr = b.nr_sequencia
			and		b.nr_seq_exame = c.nr_seq_exame
			and		b.nr_prescricao = nr_prescricao_p
			and		b.nr_sequencia = nr_seq_prescr_w
			and		(c.nr_ordem_amostra IS NOT NULL AND c.nr_ordem_amostra::text <> '')
			and		(b.nr_seq_superior IS NOT NULL AND b.nr_seq_superior::text <> '');

			if (nr_ordem_amostra_w IS NOT NULL AND nr_ordem_amostra_w::text <> '') then
				nr_seq_int_prescr_w := nr_seq_prescr_ww;
			else
				select	min(nr_seq_prescr_proc_mat)
				into STRICT	nr_seq_prescr_proc_mat_w
				from	prescr_proc_mat_item
				where	nr_prescricao = nr_prescricao_p
				and		nr_seq_prescr = nr_seq_prescr_w;

				select	max(substr(nr_seq_int_prescr,1,2))
				into STRICT	nr_seq_int_prescr_w
				from	prescr_proc_material
				where	nr_sequencia = nr_seq_prescr_proc_mat_w;

			end if;

			select	LPAD(coalesce(nr_seq_int_prescr_w,00),2,0)
			into STRICT	nr_seq_int_prescr_ww
			;

			nr_controle_exame_w := nr_seq_zenite_w||nr_seq_int_prescr_ww;

		else

			if (cd_equipamento_w > 0) then

				select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
				into STRICT	ie_gera_controle
				from	prescr_procedimento a,
						lab_exame_equip b,
						material_exame_lab d
				where	a.nr_seq_exame    = b.nr_seq_exame
				and		b.cd_equipamento  = cd_equipamento_w
				and 	a.cd_material_exame = d.cd_material_exame
				and		a.nr_prescricao   = nr_prescricao_p
				and (b.nr_seq_material = d.nr_sequencia or coalesce(b.nr_seq_material::text, '') = '')
				and		a.nr_sequencia = nr_seq_prescr_w;

				if (ie_gera_controle = 'S') then

					select 	LPAD((max(qt_atual) + 1),5,0),
							max(qt_maximo),
							max(qt_atual) + 1
					into STRICT	cd_seq_estab_w,
							qt_maximo_w,
							qt_atual_w
					from	REGRA_CONTROLE_PRESCR
					where	cd_estabelecimento = cd_estabelecimento_p
					and		nr_sequencia = nr_sequencia_w;

					if (qt_maximo_w > 0) 		and (qt_atual_w > qt_maximo_w) 	then
						qt_atual_w	:= 8902000000;
					end if;

					update	regra_controle_prescr
					set		qt_atual = qt_atual_w
					where	nr_sequencia = nr_sequencia_w;

					nr_controle_exame_w := qt_atual_w;
				end if;
			end if;
		end if;

		if 	(ie_opcao_w = 0 AND ie_opcao_p = 1) or (ie_opcao_w = 4) then

				select	coalesce(max('S'),'N')
				into STRICT	ie_retorno_w
				from	prescr_procedimento
				where	nr_controle_exame = nr_controle_exame_w
				and		nr_prescricao <> nr_prescricao_p;

				if (ie_retorno_w = 'N') then

					update	prescr_procedimento
					set		nr_controle_exame = nr_controle_exame_w
					where	nr_prescricao = nr_prescricao_p
					and		nr_sequencia = nr_seq_prescr_w
					and		coalesce(nr_controle_exame::text, '') = '';
				else
					ie_prescr_control_dup_w := 'S';
				end if;

		elsif (ie_opcao_p = 2 and ie_opcao_w <> 4) then -- Sérgio Franco
			if (ie_gera_controle = 'S') then

				update	prescr_procedimento
				set		nr_controle_exame = nr_controle_exame_w
				where	nr_prescricao = nr_prescricao_p
				and		nr_sequencia = nr_seq_prescr_w
				and		coalesce(nr_controle_exame::text, '') = '';

			end if;
		else

			update	prescr_procedimento
			set		nr_controle_exame = nr_controle_exame_w
			where	nr_prescricao = nr_prescricao_p
			and		nr_sequencia = nr_seq_prescr_w
			and		coalesce(nr_controle_exame::text, '') = '';

		end if;

		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

		END LOOP;
	close c01;
ie_prescr_control_dup_p := ie_prescr_control_dup_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lab_gerar_nr_controle_exame ( nr_prescricao_p bigint, cd_estabelecimento_p bigint, ie_opcao_p bigint, ie_prescr_control_dup_p INOUT text) FROM PUBLIC;

