CREATE OR REPLACE PROCEDURE lab_gravar_log_exame ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, cd_estabelecimento_p bigint, cd_exame_p text, ds_erro_p text, nm_usuario_p text) AS $body$
DECLARE


ds_erro_w	varchar(2000);
nr_sequencia_w	bigint;


BEGIN
ds_erro_w := substr(ds_erro_p,1,1800);

select 	nextval('lab_log_webservice_seq')
into STRICT	nr_sequencia_w
;

if (coalesce(nr_seq_prescr_p::text, '') = '') then
	ds_erro_w := substr(WHEB_MENSAGEM_PCK.get_texto(278875,null)|| cd_exame_p||' - '||ds_erro_w,1,2000);
end if;

if (ds_erro_p IS NOT NULL AND ds_erro_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	insert into lab_log_webService( nr_sequencia,
					 cd_estabelecimento,
					 nr_prescricao,
					 nr_seq_prescr,
					 ds_log,
					 dt_atualizacao,
					 nm_usuario,
					 dt_atualizacao_nrec,
					 nm_usuario_nrec
					)
	values ( nr_sequencia_w,
					  cd_estabelecimento_p,
					  nr_prescricao_p,
					  nr_seq_prescr_p,
					  ds_erro_w,
					  clock_timestamp(),
					  nm_usuario_p,
					  clock_timestamp(),
					  nm_usuario_p
					);

end if;

commit;

end;
