SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_gravar_seq_amostra (nr_prescricao_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE



ie_padrao_amostra_w	varchar(5);
nr_seq_lab_w		varchar(20);
nr_seq_prescr_proc_w	bigint;
nr_seq_grupo_w		bigint;
nr_seq_material_w		bigint;
nr_amostra_w		integer;
cd_barras_w		varchar(50);
dt_liberacao_w		varchar(8);
nr_seq_prescr_w		integer;
nr_seq_frasco_w		bigint;

c01 CURSOR FOR
	SELECT  nr_sequencia,
		nr_seq_grupo,
		nr_amostra,
		nr_seq_material,
		nr_seq_frasco
	from	prescr_proc_material
	where 	nr_prescricao = nr_prescricao_p;

c02 CURSOR FOR
	SELECT 	nr_seq_lab,
		a.nr_sequencia
	from 	prescr_procedimento a,
		exame_laboratorio b,
		exame_lab_material c,
		material_exame_lab d
	where	a.cd_material_exame = d.cd_material_exame
	and	b.nr_seq_exame = c.nr_seq_exame
	and	c.nr_seq_material = d.nr_sequencia
	and	d.nr_sequencia = nr_seq_material_w
	and	a.nr_seq_exame = b.nr_seq_exame
	and	b.nr_seq_grupo = nr_seq_grupo_w
	and	((nr_seq_frasco = nr_seq_frasco_w) or (ie_padrao_amostra_w not in ('AM11F','AM10F')))
	and	a.nr_prescricao = nr_prescricao_p;



BEGIN

select  to_char(coalesce(dt_liberacao,dt_liberacao_medico), 'ddmmyyyy')
into STRICT	dt_liberacao_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select  coalesce(max(ie_padrao_amostra),'PM')
into STRICT 	ie_padrao_amostra_w
from 	lab_parametro
where 	cd_estabelecimento = cd_estabelecimento_p;

if (ie_padrao_amostra_w <> 'PM') and (ie_padrao_amostra_w <> 'PM11') and (ie_padrao_amostra_w <> 'PM13') then

	open C01;
	loop
	fetch c01 into	nr_seq_prescr_proc_w,
			nr_seq_grupo_w,
			nr_amostra_w,
			nr_seq_material_w,
			nr_seq_frasco_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */

		open C02;
		loop
		fetch c02 into	nr_seq_lab_w,
				nr_seq_prescr_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */

			if (ie_padrao_amostra_w IN ('AMO9','AMO10','AMO11','AM11F','AM10F','AMO13')) then
				cd_barras_w := nr_seq_prescr_proc_w;
			elsif (ie_padrao_amostra_w = 'EAM10') then
				cd_barras_w := lpad(substr(coalesce(cd_estabelecimento_p,1),1,2),2,0) || nr_seq_prescr_proc_w;
			else
				cd_barras_w := dt_liberacao_w || lpad(nr_seq_grupo_w,2,'0') || lpad(nr_seq_lab_w,20,'0') || nr_amostra_w;
			end if;

			begin

			update prescr_proc_material
			set cd_barras = cd_barras_w
			where nr_sequencia = nr_seq_prescr_proc_w;

			exception
			when others then

				update 	prescr_procedimento
				set 	nr_seq_lab  = NULL
				where 	nr_prescricao = nr_prescricao_p
				and	nr_sequencia = nr_seq_prescr_w;

			end;

		end loop;
		close c02;

	end loop;
	close c01;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
